.shiverr a {
    display: block;
    animation: shiver 0.5s infinite;
    font-size: 250%;
    text-align: center;
}
.shiver {
    -webkit-animation-name: shiver;
    animation-name: shiver;
}
@keyframes shiver {
  0% { transform: translate(1px, 1px) rotate(0deg); opacity:100%; }
  10% { transform: translate(-1px, -2px) rotate(-1deg); opacity:90%;}
  20% { transform: translate(-3px, 0px) rotate(1deg); opacity:80%;}
  30% { transform: translate(3px, 2px) rotate(0deg); opacity:70%;}
  40% { transform: translate(1px, -1px) rotate(1deg); opacity:60%;}
  50% { transform: translate(-1px, 2px) rotate(-1deg); opacity:50%; }
  60% { transform: translate(-3px, 1px) rotate(0deg); opacity:60%;}
  70% { transform: translate(3px, 1px) rotate(-1deg); opacity:70%;}
  80% { transform: translate(-1px, -1px) rotate(1deg); opacity:80%;}
  90% { transform: translate(1px, 2px) rotate(0deg); opacity:90%;}
  100% { transform: translate(1px, -2px) rotate(-1deg); opacity:100%; }
}

/* terminal */
div.terminal{border:solid 3px #bbb;border-radius:16px;background-color:#131;background-image:radial-gradient(ellipse 1000% 100% at 50% 90%,transparent,#121);background-position:center;box-shadow:inset 0 0 10em 1em rgba(0,0,0,.5);overflow:hidden}div.terminal blockquote{background-color:#000;border:double 3px #80ff80}div.scanline{margin-top:-40%;width:100%;height:60px;position:relative;pointer-events:none;-webkit-animation:scan 12s linear 0s infinite;animation:scan 12s linear 0s infinite;background:linear-gradient(to bottom,rgba(56,112,82,0),rgba(56,112,82,.1))!important}div.text{color:rgba(128,255,128,.8);padding-left:2em;padding-right:2em;padding-top:35%;font-family:monospace;font-size:1.2em}@-webkit-keyframes scan{from{transform:translateY(-10%)}to{transform:translateY(5000%)}}@keyframes scan{from{transform:translateY(-10%)}to{transform:translateY(5000%)}}div.text a{color:#90ee90;text-decoration:none;background:0 0}div.text a.newpage{color:#90ee90;text-decoration:none;background:0 0}div.text a:hover{color:#131;text-decoration:underline;background-color:#80ff80;padding:1px}div.text a:hover::before{content:"> "}
