[[module css]]

@charset "utf-8";
/*ç‰ˆå¤´*/
div#container-wrap {
    background-image: url(http://scpsandboxcn.wikidot.com/local--files/collab:sleepless-voyage-css/%E9%85%92%E5%90%A7%E7%89%88%E5%A4%B4);
    background-repeat: repeat-x;
}
#header h1 a {
    line-height: 0px;
    color: #eee;
    font-family: BauhausLTDemi, Simhei, Arial, sans-serif;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
    display: none;
}
#header h2 span {
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #f0f0c0;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
    display: none;
}
/*ä¸ªäººä¿¡æ¯*/
#login-status {
    color: #aaa;
    font-size: 90%;
    z-index: 30;
    display: none;
}
.printuser a {
    margin-right: -1px;
    color: #ffb441 !important;
}
/*å†å²è®°å½•*/
table.page-history td.optionstd a {
    border: 1px solid #ffb441;
    padding: 0 3px;
    text-decoration: none;
    background: #b02546;
    color: #ffaf40;
    transition: .4s all;
}
table.page-history td.optionstd a:hover {
    background: #ffaf40;
    color: #fff;
}
.pager .current {
    padding: 0 7px;
    margin: 0 3px;
    font-weight: bold;
    background-color: #ffb441;
    color: #FFF;
    border: 1px solid #ffb441;
}
.pager a {
    padding: 0 7px;
    margin: 0 3px;
    border: 1px solid #ffb441;
    text-decoration: none;
    background: #b02546;
    color: #ffb441;
    transition: .5s all;
}
.pager a:hover {
    background: #ffb441;
    color: #fff;
    text-decoration: none;
}
/*é¡¶æ */
#top-bar ul li a {
    border-left: solid 1px rgba(64,64,64,.1);
    border-right: solid 1px rgba(64,64,64,.1);
    text-decoration: none;
    padding-top: 10px;
    padding-bottom: 10px;
    line-height: 1px;
    max-height: 1px;
    overflow: hidden;
    display: none;
}
/*æœç´¢æ */
#search-top-box {
    position: absolute;
    top: 79px;
    right: 9px;
    width: 250px;
    text-align: right;
    display: none;
}
/*ä¾§æ */
#side-bar .side-block {
    padding: 10px;
    border: 3px double #ffaf40;
    border-radius: 10px;
    box-shadow: 0 2px 6px #b02546;
    margin-bottom: 15px;
    background: #b02546 !important;
}
#side-bar .side-block a {
    color: #ffaf40;
    background: none;
}
#side-bar .heading {
    color: #ffaf40;
    font-size: 8pt;
    font-weight: bold;
    border-bottom: #ffaf40 1px solid;
}
hr {
    margin: 1em 2em;
    padding: 0;
    height: 1px;
    border: none;
    color: #DDD;
    background-color: #ffaf40;
}
#side-bar .side-block a:hover {
    text-decoration: none;
}
#side-bar .collapsible-block-link {
    margin-left: 15px;
    font-weight: bold;
    border: none;
}
#side-bar .collapsible-block-link:hover {
    background: none;
}
#side-bar img, #side-bar .collapsible-block-folded {
    -webkit-filter: hue-rotate(0deg) sepia(100%);
    ffilter: hue-rotate(0deg) sepia(100%);
}
#side-bar:target .close-menu {
    display: block;
    position: fixed;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background: #1f0c28bd 1px 1px repeat !important;
    z-index: -1;
}
/*è¯„åˆ†*/
.page-rate-widget-box .rate-points {
    background-color: #b02546 !important;
    border: solid 1px #ffb441;
    border-right: 0;
    border-radius: 5px 0 0 5px;
    color: #ffb441;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #b02546;
    border-top: solid 1px #ffb441;
    border-bottom: solid 1px #ffb441;
    font-weight: bold;
}
.page-rate-widget-box .cancel {
    background-color: #b02546;
    border: solid 1px #ffb441;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: #b02546;
    text-transform: uppercase;
    color: #ffb441;
    transition: .5s all;
    border-top: 1px solid #ffb441;
    border-right: solid 1px #ffb441;
    border-bottom: solid 1px #ffb441;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    background: transparent;
    color: #ffaf40;
    padding: 0 4px;
    margin: 0 1px;
    transition: .3s all;
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    background: transparent;
    color: #fff;
    text-decoration: none;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: transparent;
    color: #fff;
    text-decoration: none;
}
/*å¼•ç”¨æ¡†*/
blockquote {
    border: 3px double #ffaf40;
    padding: 0 1em;
    background-color: transparent;
    border-radius: 10px;
    color: #ffaf40;
}
/*æŠ˜å */
.collapsible-block-link {
    border: double 3px #ffaf40;
    color: #ffaf40;
    background: #b02546;
    transition: .5s all;
}
.collapsible-block-link:hover {
    background: #ffaf40;
    color: #fff;
    border: double 3px #fff;
    text-decoration: none;
}
/*å›¾ç‰‡*/
.scp-image-block {
    border: solid 1px #ffaf40;
    box-shadow: 0 1px 6px #ffaf40;
}
.scp-image-block .scp-image-caption {
    background-color: #2d1139;
    border-top: solid 1px #ffaf40;
    padding: 2px 0;
    font-size: 80%;
    font-weight: bold;
    text-align: center;
}
.scp-image-block img {
    border: 0;
    transition: .3s all;
}
.scp-image-block img:hover {
    transform: scale(1.1);
}
/*è¡¨æ ¼*/
table.wiki-content-table th {
    border: 3px double #ffb441;
    padding: 0.3em 0.7em;
    background-color: #b02546;
}
table.wiki-content-table td {
    border: 3px double #fab23c;
    padding: 0.3em 0.7em;
}
/*tabæ ¼å­*/
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    font-family: 'Palatino Linotype', 'Book Antiqua', 'SimSun Regular', Palatino, serif;
    background-color: #fff;
    background-size: cover;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 0px;
    border-top: solid 1px #ffb441;
    border-left: solid 1px #ffb441;
    border-right: solid 1px #ffb441;
    transition: color .5s,background .5s;
}
.yui-navset .yui-nav a:hover, .yui-navset .yui-nav a:focus {
    font-family: 'Palatino Linotype', 'Book Antiqua', 'SimSun Regular', Palatino, serif;
    color: #ffb441;
    background: #b02546;
}
.yui-navset .yui-nav a em, .yui-navset .yui-navset-top .yui-nav a em {
    border: none;
    border-width: 1px 0 0;
    padding: 0.25em .75em;
    left: 0;
    right: 0;
    bottom: 0;
    top: -1px;
    position: relative;
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-color: #ffb441;
}
.yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
    border: 1px solid #ffb441;
    padding: 0.25em 0.5em;
    border-top: 0;
}
.yui-navset .yui-content {
    background-color: #2d1139;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    font-family: 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
    border-width: 0px;
    padding: 2px 20px 0px 20px;
    margin-top: 0px;
    border-top: none;
    border-left: 1px solid #ffb441;
    border-right: solid 1px #ffb441;
    background: #ffb441;
    color: #fff;
}
.yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
    font-family: 'Palatino Linotype', 'Book Antiqua', 'SimSun Regular', Palatino, serif;
    color: #ffb441;
}
.yui-navset li a {
    color: #ffb441;
}
/*ç¬”è®°æ */
div.wiki-note {
    text-align: center;
    border: double 3px #ffaf40;
    color: #ffaf40;
}
/*codeæ¡†*/
.code {
    border: 3px double #ffaf40;
    background-color: transparent;
    font-family: 'Andale Mono', 'Courier New', Courier, monospace;
    padding: 0 1em;
    margin: 0.4em 0;
    overflow: auto;
    color: #ffaf40;
}
/*åº•éƒ¨æŒ‰é’®*/
.page-options-bottom a {
    padding: 0 4px;
    color: #ffaf40;
    border: double 3px #ffaf40;
    background: #b02546;
    transition: .3s all;
}
.page-options-bottom a:hover {
    background: #ffb441;
    color: #fff;
    border: double 3px #fff;
    text-decoration: none;
}
/*å…³æ³¨è€…*/
.page-watch-options a {
    text-decoration: none;
    color: #ffb441;
    transition: .3s all;
}
.page-watch-options a:hover {
    background: #ffb441;
    color: #fff;
    text-decoration: none;
}
/*ç½‘é¡µæºä»£ç */
.page-source {
    border: 3px double #ffaf40;
    padding: 1em 2em;
    background: #b02546;
}
a.action-area-close {
    float: right;
    margin: 2px 2px;
    font-size: 95%;
    padding: 0 0.7em;
    color: #ffb441;
    border: double 3px #ffb441;
    background: #b02546;
    transition: .3s all;
}
a.action-area-close:hover {
    background-color: #ffb441;
    border: double 3px #fff;
    color: #fff;
    text-decoration: none;
}
/*è„šæ³¨*/
.footnotes-footer {
    margin: 20px 0px 20px 0px;
    padding: 0.5em;
    border-left: .75rem solid #ffb441;
    border-right: 1px solid #ffb441;
    border-top: 1px solid #ffb441;
    border-bottom: 1px solid #ffb441;
    height: 1%;
    background: #b02546;
    color: #ffb441;
}
.footnotes-footer a {
    color: #ffb441;
}
a.footnoteref {
    display: inline-block;
    color: #ffb441 !important;
}
/*æç¤ºæ */
.hovertip {
    width: 20em;
    border: 2px solid #ffb441 !important;
    background-color: #b02546;
    z-index: 100;
}
/*ç¼–è¾‘æ */
input.text {
    border: 1px double #ffb441;
    margin: 0 1px;
    padding: 1px 2px;
    background-color: #b02546;
    color: #ffb441;
}
textarea {
    font-family: verdana,arial,helvetica,sans-serif;
    padding: 2px;
    font-size: 100%;
    border: 1px double #ffb441;
    background-color: #b02546;
    color: #ffb441;
}
.change-textarea-size a {
    color: #ffb441;
    background-color: #b02546;
    border: 1px solid #ffb441;
    border-width: 0 1px 1px 1px;
    padding: 2px 4px;
    font-size: 120%;
    text-decoration: none;
    transition: .5s all;
}
.change-textarea-size a:hover {
    background: #fff;
    color: #ffb441;
    text-decoration: none;
}
#lock-info {
    background-color: #b02546;
    border: 1px double #ffb441;
    padding: 0.5em 1em;
    margin: 1em 0;
    color: #ffb441;
}
div.buttons input, input.button, button, file, a.button {
    margin: 0 2px;
    padding: 0px 0.5em;
    text-decoration: none;
    background-color: #b02546;
    border: 3px double #ffb441;
    color: #ffb441;
    transition: .5s all;
}
div.buttons input:hover, input.button:hover, button:hover, a.button:hover {
    background-color: #fff;
    color: #ffb441;
    text-decoration: none;
}
.edit-help-34 a {
    color: #ffaf40;
    transition: .5s all;
}
.edit-help-34 a:hover {
    text-decoration: none;
}
table.form td div.sub, form div.sub {
    font-size: 90%;
    color: #ffb441;
}
/*ä¸ªäººä¿¡æ¯*/
.owindow {
    background-color: #b02546;
    border: 3px double #ffaf40;
    text-align: left;
    position: absolute;
}
.owindow .title {
    cursor: move;
    font-size: 120%;
    font-weight: bold;
    padding: 0.2em 1em;
    background-color: transparent;
    height: 1%;
}
#owindow-1 a {
    color: #ffaf40;
}
.owindow .button-bar a {
    margin: 0.2em 0.2em;
    padding: 0.1em 1em;
    text-decoration: none;
    background-color: #b02546;
    border: 3px double #ffaf40;
    transition: .3s all;
}
.owindow .button-bar a:hover {
    background: #ffaf40;
    color: #fff !important;
    border: double 3px #fff;
    text-decoration: none;
}
/*è¶…é“¾æ¥*/
p a {
    color: #fff !important;
    transition: .5s all;
}
p a:hover {
    background: #ffb441;
    color: #fff !important;
    text-decoration: none;
}
/*æ ‡ç­¾*/
#main-content .page-tags a {
    display: inline-block;
    white-space: nowrap;
    color: #ffb441;
    transition: .3s all;
}
.page-tags span {
    border-top: 2px solid #ffb441;
    font-size: 85%;
}
#main-content .page-tags a:hover {
    background: #ffb441;
    color: #fff;
    text-decoration: none;
}
/*é¢„è§ˆ*/
.preview-message {
    position: absolute;
    right: 2em;
    border: 3px double #ffaf40;
    padding: 0.5em 1em;
    background-color: #2d113978;
    max-width: 20em;
    opacity: 0.9;
    z-index: 1;
    color: #ffaf40;
}
.preview-message a {
    color: #ffaf40;
    transition: .3s all;
}
.preview-message a:hover {
    background: #ffaf40;
    color: #fff;
    text-decoration: none;
}
/*é™„ä»¶*/
table.page-files .highlight {
    background-color: #b02546;
    border: 3px double #ffaf40;
}
td a {
    color: #ffaf40;
}
#show-upload-button {
    color: #ffaf40;
}
/*æ–‡ç« éƒ¨åˆ†*/
body {
    font-size: 0.80em;
    color: #ffaf40;
    background: #2d1139;
}
#page-title {
    border: double 2px #ffaf40;
    text-align: center;
}
h1, #page-title {
    color: #ffaf40;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
    font-weight: normal;
}
/*åº•æ */
#footer {
    clear: both;
    font-size: 77%;
    background: #59042b;
    color: #ffb045;
    margin-top: 15px;
    padding: 3px 10px;
}
#footer a {
    color: #ffaf40;
    background: transparent;
}
#license-area {
    padding: 0.5em 0;
    color: #ffe17b;
    text-align: center;
}
#license-area a {
    color: #ffb441;
}
/*ä¸€ä»½æƒŠå–œ*/
.unmargined, .unmargined + hr {
    filter: hue-rotate(0deg) sepia(100%);
}
}

