    background-position: 20% 20%;
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: linear-gradient(to bottom,
      hsl(335, 55%, 55%),
      hsl(335, 55%, 50%));
}

div#header {
    background-image: url("https://scp-wiki.wdfiles.com/local--files/theme%3Ajust-girly-things/JGTLOGONEW.png");
    filter: drop-shadow(.1rem .1rem .063rem hsl(335, 100%, 8%));
}

#header h1 a {
    color: transparent;
    text-shadow: none;
}

#header h1 a::before {
    content: "ÁªùËµûÁãÇÊö¥ÊñáÂ≠¶Â∞èÂ±ã";
    font-family: 'YouYuan', cursive;
    color: hsl(335, 90%, 95%);
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: "ÂîîÂßÜ~ÊèâÊèâÊêìÊêìÊçèÊçèÔºÅ";
    color: hsl(335, 90%, 70%);
