    letter-spacing: 2px;
    padding-left: 4em;
}

/*-------------- LOG-IN INFO -------------- */
#login-status,
#login-status a {
    color: hsl(335, 100%, 98%);
}

#login-status ul a:hover {
   font-weight: bold;
}

#account-topbutton {
    background: transparent;
    color: hsl(335, 100%, 14%);
    border: none;
}

#account-options {
    background: hsl(335, 90%, 95%);
    border-color: hsl(335, 55%, 55%);
}

/* -------------- SEARCH-BAR -------------- */
#search-top-box-input {
     border: solid 1px #e5e5e5;
     color: #e5e5e5;
     background-color: hsl(335, 90%, 30%);
}

#search-top-box-input:hover,
#search-top-box-input:focus {
     background-color: hsl(335, 90%, 45%);
}

#search-top-box-form input[type=submit] {
     border: solid 1px #e5e5e5;
     color: #e5e5e5;
     background-color: hsl(335, 90%, 50%);
     background: linear-gradient(
       to bottom,
       hsl(335, 90%, 60%),
       hsl(335, 90%, 50%),
       hsl(335, 90%, 40%));
     box-shadow: none;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
     background-color: hsl(335, 90%, 60%);
     background: linear-gradient(
       to bottom,
       hsl(335, 90%, 70%),
       hsl(335, 90%, 60%),
       hsl(335, 90%, 50%));
     box-shadow: none;
}

/* -------------- TOP-BAR -------------- */
#top-bar ul li ul {
    border: solid 1px hsl(335, 90%, 85%);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid hsl(335, 90%, 85%);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: hsl(335, 90%, 95%);
}

/* -------------- BODY -------------- */
body {
    background-color: #ffe3ee;
font-family: 'Courgette','ZCOOL Kuaile', normal;
    font-size: 0.8em;
    color: #ff69ab;
}

#page-title, h1 {
    color: hsl(335, 95%, 45%)
}

#page-title {
    border-bottom-color: hsl(335, 90%, 75%);
}

/* -------------- SIDE-BAR -------------- */
#side-bar .side-block {
    border: 1px solid hsl(335, 90%, 25%);
    box-shadow: 0 2px 6px rgba(121, 6, 54,.5);
    color: hsl(335, 90%, 25%);
