[[include :scp-wiki-cn:theme:linkage]]

[[module css]]
/* Content */
#page-content {
  display: -webkit-box;
  display: -webkit-flex;
  display: -moz-box;
  display: -ms-flexbox;
  display: flex;
}
.menuWrapWrap, .contentWrap {
  -webkit-box-flex: 1;
  -webkit-flex-grow: 1;
  -moz-box-flex: 1;
  -ms-flex-positive: 1;
  flex-grow: 1;
  -webkit-flex-shrink: 0;
  -ms-flex-negative: 0;
  flex-shrink: 0;
  width: 100%;
}
.menuWrapWrap {
  display: -webkit-box;
  display: -webkit-flex;
  display: -moz-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-pack: end;
  -webkit-justify-content: flex-end;
  -moz-box-pack: end;
  -ms-flex-pack: end;
  justify-content: flex-end;
  -webkit-box-align: end;
  -webkit-align-items: flex-end;
  -moz-box-align: end;
  -ms-flex-align: end;
  align-items: flex-end;
}
.contentWrap {
  position: relative;
  left: -100%;
}

/* Table */
table.listpages-table,
table.timeline {
  width: 100%;
  word-break: break-word;
}
table.listpages-table th {
  background-color: #666;
  color: #fff;
}
table.listpages-table td {
  vertical-align: top;
}
table.listpages-table td:not(:first-child) {
  text-align: center;
}
table.listpages-table tr:nth-child(2n+1) td {
  background-color: rgba(210, 210, 210, .5);
}
table.timeline {
  border-spacing: 0;
}
table.timeline td {
  vertical-align: top;
}
table.timeline td.label {
  font-size: 1.5em;
  font-weight: bold;
  text-align: center;
}
table.timeline td.left {
  border-right: solid 1px #999;
  width: 50%;
  text-align: right;
  padding: 5px 6px 5px 0;
}
table.timeline td.right {
  padding: 5px 0 5px 6px;
}
table.timeline div.crucial {
  font-family: monospace;
  font-size: 1.1em;
  color: #999;
}
table.timeline div.title {
  font-size: 1.25em;
}
table.timeline div.description {
  display: inline-block;
  text-align: left;
  padding: 2px 0 8px;
}
table.timeline div.description p:first-child {
  margin-top: 0;
}

/* Fragment */
.pointer {
  display: none;
}
.fragmentWrapWrap {
  display: -webkit-box;
  display: -webkit-flex;
  display: -moz-box;
  display: -ms-flexbox;
  display: flex;
  overflow: hidden;
  -webkit-mask: linear-gradient(to right, rgba(255, 255, 255, 0), rgba(255, 255, 255, 1) 4%, rgba(255, 255, 255, 1) 96%, rgba(255, 255, 255, 0) 100%);
  mask: linear-gradient(to right, rgba(255, 255, 255, 0), rgba(255, 255, 255, 1) 4%, rgba(255, 255, 255, 1) 96%, rgba(255, 255, 255, 0) 100%);
}
.fragmentWrapWrap .mask {
  position: relative;
  z-index: 1;
  -webkit-flex-shrink: 0;
  -ms-flex-negative: 0;
  flex-shrink: 0;
  width: 4%;
}
.fragmentWrap {
  display: -webkit-box;
  display: -webkit-flex;
  display: -moz-box;
  display: -ms-flexbox;
  display: flex;
  -webkit-flex-shrink: 0;
  -ms-flex-negative: 0;
  flex-shrink: 0;
  width: 92%;
  -webkit-transition: -webkit-transform .3s ease-in-out;
  -moz-transition: -moz-transform .3s ease-in-out;
  -ms-transition: -ms-transform .3s ease-in-out;
  -o-transition: -o-transform .3s ease-in-out;
  transition: transform .3s ease-in-out;
}
.fragment {
  -webkit-box-flex: 1;
  -webkit-flex-grow: 1;
  -moz-box-flex: 1;
  -ms-flex-positive: 1;
  flex-grow: 1;
  -webkit-flex-shrink: 0;
  -ms-flex-negative: 0;
  flex-shrink: 0;
  box-sizing: border-box;
  width: 100%;
  padding: 0 .5em;
}
.fragment .subtitle {
  padding-right: 1em;
  text-align: right;
  color: #aaa;
}
#u-p-1:target ~ .fragmentWrapWrap .fragmentWrap {
  -webkit-transform: translate(-100%);
  -moz-transform: translate(-100%);
  -ms-transform: translate(-100%);
  -o-transform: translate(-100%);
  transform: translate(-100%);
}
#u-p-2:target ~ .fragmentWrapWrap .fragmentWrap {
  -webkit-transform: translate(-200%);
  -moz-transform: translate(-200%);
  -ms-transform: translate(-200%);
  -o-transform: translate(-200%);
  transform: translate(-200%);
}
#u-p-3:target ~ .fragmentWrapWrap .fragmentWrap {
  -webkit-transform: translate(-300%);
  -moz-transform: translate(-300%);
  -ms-transform: translate(-300%);
  -o-transform: translate(-300%);
  transform: translate(-300%);
}
#u-p-4:target ~ .fragmentWrapWrap .fragmentWrap {
  -webkit-transform: translate(-400%);
  -moz-transform: translate(-400%);
  -ms-transform: translate(-400%);
  -o-transform: translate(-400%);
  transform: translate(-400%);
}

/* Menu */
.menuWrap {
  position: -webkit-sticky;
  position: sticky;
  right: 0;
  bottom: 0;
  width: 6em;
  height: 6em;
  border-radius: 100% 0 0 0;
  overflow: hidden;
  z-index: 9;
  -webkit-transition-delay: .8s;
  -moz-transition-delay: .8s;
  -ms-transition-delay: .8s;
  -o-transition-delay: .8s;
  transition-delay: .8s;
  -webkit-transition-property: width, height;
  -moz-transition-property: width, height;
  -ms-transition-property: width, height;
  -o-transition-property: width, height;
  transition-property: width, height;
}
.menuWrap:hover {
  width: 24em;
  height: 24em;
  -webkit-transition-delay: 0s;
  -moz-transition-delay: 0s;
  -ms-transition-delay: 0s;
  -o-transition-delay: 0s;
  transition-delay: 0s;
}
.menuWrap ul {
  margin: 0;
  padding: 0;
  list-style: none;
  position: absolute;
  right: 0;
  bottom: 0;
  width: 0;
  height: 0;
}
.menuWrap ul li {
  position: absolute;
  right: 0;
  bottom: 0;
  border-radius: 100% 0 0 0;
}
.menuWrap > ul > li {
  width: 6em;
  height: 6em;
  background-color: #404040;
  color: #fff;
  cursor: pointer;
  -webkit-transition: .3s ease-in-out;
  -moz-transition: .3s ease-in-out;
  -ms-transition: .3s ease-in-out;
  -o-transition: .3s ease-in-out;
  transition: .3s ease-in-out;
  -webkit-transition-delay: .5s;
  -moz-transition-delay: .5s;
  -ms-transition-delay: .5s;
  -o-transition-delay: .5s;
  transition-delay: .5s;
}
.menuWrap:hover > ul > li {
  width: 11em;
  height: 11em;
  -webkit-transition-delay: 0s;
  -moz-transition-delay: 0s;
  -ms-transition-delay: 0s;
  -o-transition-delay: 0s;
  transition-delay: 0s;
}
.menuWrap > ul > li > span.fa {
  display: block;
  position: absolute;
  right: 0;
  bottom: 0;
  width: 1.2em;
  height: 1.2em;
  font-size: 3em;
  -webkit-transition: .3s ease-in-out;
  -moz-transition: .3s ease-in-out;
  -ms-transition: .3s ease-in-out;
  -o-transition: .3s ease-in-out;
  transition: .3s ease-in-out;
  -webkit-transition-delay: .5s;
  -moz-transition-delay: .5s;
  -ms-transition-delay: .5s;
  -o-transition-delay: .5s;
  transition-delay: .5s;
}
.menuWrap:hover > ul > li > span.fa {
  font-size: 5.5em;
  -webkit-transition-delay: 0s;
  -moz-transition-delay: 0s;
  -ms-transition-delay: 0s;
  -o-transition-delay: 0s;
  transition-delay: 0s;
}
.menuWrap > ul ul {
  z-index: -1;
}
.menuWrap > ul ul li {
  -webkit-transform-origin: right bottom;
  -moz-transform-origin: right bottom;
  -ms-transform-origin: right bottom;
  -o-transform-origin: right bottom;
  transform-origin: right bottom;
  -webkit-transform: rotate(95deg);
  -moz-transform: rotate(95deg);
  -ms-transform: rotate(95deg);
  -o-transform: rotate(95deg);
  transform: rotate(95deg);
  -webkit-transition: -webkit-transform .5s ease-in-out;
  -moz-transition: -moz-transform .5s ease-in-out;
  -ms-transition: -ms-transform .5s ease-in-out;
  -o-transition: -o-transform .5s ease-in-out;
  transition: transform .5s ease-in-out;
}
.menuWrap:hover > ul ul li {
  -webkit-transition-delay: .3s;
  -moz-transition-delay: .3s;
  -ms-transition-delay: .3s;
  -o-transition-delay: .3s;
  transition-delay: .3s;
}
.menuWrap > ul ul li a {
  display: block;
  width: 100%;
  height: 100%;
  box-sizing: border-box;
  padding-left: .8em;
  border-left: .8em solid transparent;
  border-radius: 100% 0 0 0;
  color: #000;
  text-decoration: none;
  -webkit-transition: border-color .1s ease-in-out;
  -moz-transition: border-color .1s ease-in-out;
  -ms-transition: border-color .1s ease-in-out;
  -o-transition: border-color .1s ease-in-out;
  transition: border-color .1s ease-in-out;
}
.menuWrap > ul ul li a:hover {
  border-color: rgb(var(--dark-accent));
}
.menuWrap > ul ul li a > span {
  display: block;
  position: absolute;
  bottom: 0;
  width: 100%;
  font-size: 1.4em;
  -webkit-transform-origin: right bottom;
  -moz-transform-origin: right bottom;
  -ms-transform-origin: right bottom;
  -o-transform-origin: right bottom;
  transform-origin: right bottom;
  -webkit-transform: rotate(5deg);
  -moz-transform: rotate(5deg);
  -ms-transform: rotate(5deg);
  -o-transform: rotate(5deg);
  transform: rotate(5deg);
}
.menuWrap > ul > li > ul > li:nth-of-type(1) {
  width: 17em;
  height: 17em;
}
.menuWrap > ul > li > ul > li:nth-of-type(1) a {
  background-color: #e7e7eb;
}
.menuWrap:hover > ul > li > ul > li:nth-of-type(1) {
  -webkit-transform: rotate(0deg);
  -moz-transform: rotate(0deg);
  -ms-transform: rotate(0deg);
  -o-transform: rotate(0deg);
  transform: rotate(0deg);
}
.menuWrap > ul > li > ul > li:nth-of-type(2) {
  width: 18em;
  height: 18em;
}
.menuWrap > ul > li > ul > li:nth-of-type(2) > a {
  background-color: #d6d6da;
}
.menuWrap:hover > ul > li > ul > li:nth-of-type(2) {
  -webkit-transform: rotate(18deg);
  -moz-transform: rotate(18deg);
  -ms-transform: rotate(18deg);
  -o-transform: rotate(18deg);
  transform: rotate(18deg);
}
.menuWrap > ul > li > ul > li:nth-of-type(3) {
  width: 19.5em;
  height: 19.5em;
}
.menuWrap > ul > li > ul > li:nth-of-type(3) > a {
  background-color: #c5c5c9;
}
.menuWrap:hover > ul > li > ul > li:nth-of-type(3) {
  -webkit-transform: rotate(36deg);
  -moz-transform: rotate(36deg);
  -ms-transform: rotate(36deg);
  -o-transform: rotate(36deg);
  transform: rotate(36deg);
}
.menuWrap > ul > li > ul > li:nth-of-type(4) {
  width: 21.5em;
  height: 21.5em;
}
.menuWrap > ul > li > ul > li:nth-of-type(4) > a {
  background-color: #b4b4b8;
}
.menuWrap:hover > ul > li > ul > li:nth-of-type(4) {
  -webkit-transform: rotate(54deg);
  -moz-transform: rotate(54deg);
  -ms-transform: rotate(54deg);
  -o-transform: rotate(54deg);
  transform: rotate(54deg);
}
.menuWrap > ul > li > ul > li:nth-of-type(5) {
  width: 24em;
  height: 24em;
}
.menuWrap > ul > li > ul > li:nth-of-type(5) > a {
  background-color: #a3a3a7;
}
.menuWrap:hover > ul > li > ul > li:nth-of-type(5) {
  -webkit-transform: rotate(72deg);
  -moz-transform: rotate(72deg);
  -ms-transform: rotate(72deg);
  -o-transform: rotate(72deg);
  transform: rotate(72deg);
}
