[[html]]
<!DOCTYPE html>
<html>
<center>
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <style>
        body{
            background-color: black;
        }
        div{
            width: 170px;
            height:20px;
            background-color:brown;
            border-radius:5px;
            font-size: 0;
            overflow: hidden;
        }
        p{
            width:80px;
            height:20px;
            font-size:15px;
            color:white;
            font-weight:bold;
            line-height:20px;
            display:inline-block;
            text-align:center;
            vertical-align: top;
            margin: 0;
            padding: 0;
        }
        button{
            width:30px;
            height:19px;
            background-color: papayawhip;
            display:inline-block;
            font-size:13px;
            line-height:20px;
            text-align:center;
            vertical-align: top;
            border: none;
            outline: none;
            background-color:white;
            color: brown;
            margin: 0.5px 0;
        }
        div>button:nth-of-type(3){
            background-color: brown;
            color: lightgray;
            font-size: 20px;
            margin: 0;
            height: 20px;
        }
    </style>
</head>
<body>
    <div>
        <p>评分：<span>0</span></p>
        <button>+</button>
        <button>-</button>
        <button>×</button>
    </div>
    <script src="https://cdn.staticfile.org/jquery/1.10.2/jquery.min.js"></script>
    <script>
        $("button").click(function(){
            var i=$(this).index();
            var n=$("p>span");
            var m=parseInt($("p>span").html());
            if(m>98){
                m=98;
            }
            else if(m<-98){
                m=-98;
            }
            switch(i){
                case 1:
                n.html(m+1);
                break;
                case 2:
                n.html(m-1);
                break;
                case 3:
                n.html(0);
           }
        });

    </script>
</body>
</center>
</html>
[[/html]]
[[/collapsible]]
[[/=]]"
