 --logo-image: url("http://scp-wiki.wdfiles.com/local--files/theme%3Asimple-yonder/logo.png");
}


.anim {
    text-align: center;
    font-weight: bold;
}
#u-start {
    display: none;
}
#u-start:target {
    display: block;
    margin: 1em 0;
}
#u-start > * {
  animation-name: fadeIn;
  animation-duration: 1s;
  animation-iteration-count: 1;
  animation-timing-function: ease-out;
  animation-fill-mode: backwards;
}
@keyframes fadeIn {
  0% {
    font-size: 0;
    opacity: 0;
    transform: translate(0,30px);
  }
  1% {
    font-size: 100%;
  }
  100% {
    opacity: 1;
    transform: translate(0,0);
  }
}
#u-start >:nth-child(1), #u-start >:nth-child(2) { animation-delay: 1s; }
