[[include :scp-wiki-cn:theme:plvs-vltra]]
[[module css]]
body {
    cursor: url('http://roger.wdfiles.com/local--files/picture-warehouse/xl-c-small.jpg'), default;
}
a {
    cursor: url('http://roger.wdfiles.com/local--files/picture-warehouse/xl-p-small.jpg'), pointer;
}
p {
    cursor: url('http://roger.wdfiles.com/local--files/picture-warehouse/xl-c-small.jpg'), text;
}
#header h1 a::before {
  content: "败局计划·再无异常";
  font-size: 80%;
  font-family: 'Vollkorn', serif;
  font-weight: 800;
  color: #ffc500;
  text-shadow: 2px 2px 2px black;
}
#header h1 a span{
    visibility: hidden;
}
#header h2 span {
    color: transparent;
    text-shadow: 0px 0px 0px;
}
#header h2 span::before {
  content: "Losing Plan · No more abnormality";
  color: white;
  font-family: 'Dancing Script', cursive;
  font-weight: 400;
  text-shadow: 2px 2px 2px black;
}
.page-rate-widget-box span.rate-points {
    /* font-size: 0; */
    letter-spacing: -12px;
    color: transparent !important;
}
.page-rate-widget-box .rate-points::before,
.page-rate-widget-box .rate-points span {
    content: "Likes：";
    font-size: 12px;
    letter-spacing: 0;
    color: #000;
}
.rate-points .number {
    font-size:0rem;
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color:#fff !important;
    border: solid #fff 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid transparent!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #000000;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #F2F2F2!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #000000!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: #000000!important;
}
#login-status {
    color: black;
    filter: grayscale(100%) saturate(0%);
    -webkit-filter: grayscale(100%) saturate(0%);
}
#login-status a {
    color: black;
}
#login-status ul a {
    color: black;
}
#account-topbutton {
     background: #ccc;
     color: black;
}
body {
    background-color: #101d2e;
}
h1,
#page-title {
    color: #ffbf00;
}

.printuser img.small {
    margin-right: 1px;
    background-image: url(http://www.wikidot.com/userkarma.php?u=1)!important;
}

[[/module]]

