<style>

@import url('https://fonts.googleapis.com/css2?family=Liu+Jian+Mao+Cao&display=swap');

@import url('https://fonts.googleapis.com/css2?family=New+Tegomin&display=swap');

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+JP:wght@900&display=swap');

@import url('http://scpko.wdfiles.com/local--files/korean-font/조선100년체.css');

#metrica {

position: absolute;   top: 0%;
  left: 0%;

width: 100vw;
height: 100vw;
}
#circle-1{
fill:none;
stroke:#ffffff;
stroke-width:0.5;
stroke-dasharray:100;
stroke-dashoffset:60;

animation:anim1 9s ease-in-out infinite;
transform-origin:center;

}
#circle-2{
fill:none;
stroke:#ffffff;
stroke-width:0.5;
stroke-dasharray:100;
stroke-dashoffset:60;

animation:anim1 7s ease-in-out infinite;
transform-origin:center;

}

@keyframes anim1 {
    0% {
        transform:rotate(0deg);
stroke-dashoffset:100;

    }

50% {
        transform:rotate(-360deg);
stroke-dashoffset:0;
    }

    100% {
        transform:rotate(360deg);
stroke-dashoffset:100;
    }
}

#wrapper {

width: 100vw;
  height: 100vw;

  position: relative;

overflow: hidden;
background: black;
  border-radius: 50vw;

}

#prayertext { position: absolute;   top: 0%;
  left: 0%; color: white; font-size: 5vh; opacity: 1; font-family: 'New Tegomin', '조선100년체', serif;}

#axisanchor {
  position: absolute;
  top: 50%;
  left: 50%;
  right: 0;
  bottom: 0;
}

#massobj-bone-0,
#massobj-bone-1,
#massobj-bone-2,
#massobj-bone-3,
#massobj-bone-4,
#massobj-bone-5,
#massobj-bone-6,
#massobj-bone-7,
#massobj-bone-8,
#massobj-bone-9,
#massobj-bone-10 {
  position: absolute;
}

#massobj-texture-0 {
color: white;

font-family: 'Noto Serif JP', 'UnBatangBold', serif;
font-weight: 900;

vertical-align:middle;
text-align: center;

  position: absolute;
  background: transparent;

  width: 4em;
  height: 8em;

  top: -4em;
  left: -2em;
  font-size: 2em;
}

#massobj-texture-1,
#massobj-texture-2,
#massobj-texture-3,
#massobj-texture-4,
#massobj-texture-5,
#massobj-texture-6,
#massobj-texture-7,
#massobj-texture-8,
#massobj-texture-9,
#massobj-texture-10 {
color: white;

font-family: 'New Tegomin',  '조선100년체', serif;

  position: absolute;
  border-radius: 50%;
  background: transparent;

  width: 1em;
  height: 1em;

  top: -1em;
  left: -0.5em;

