[[module css]]
#page-title {
    border-color: #fffbe2;
    white-space: nowrap;
}

a {
    color: #f94016;
}

a:visited {
    color: #f9b216;
}

a.newpage {
    color: #f49b42;
}

/* GLOBAL WIDTH */
body {
    color: #593116;
}
div#container-wrap {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/other-of-element/body_bg.png) top left repeat-x;
}

/* HEADER */
#header {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/other-of-element/Maple.png) 10px 35px no-repeat;
}
#search-top-box-input {
    color: #e25a00;
    background-color: #633;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #633;
}
#search-top-box-form input[type=submit] {
    background-color: #633;
    background: linear-gradient(to bottom, #966,#633,#300);
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background-color: #966;
    background: linear-gradient(to bottom, #c99,#966,#633);
}

#login-status {
    color: #e57834;
}
#login-status a {
    color: #ffb95e;
}
#login-status ul a {
    color: #ffb23f;
}

#account-topbutton {
    background: #fff9d3;
    color: #f60;
}
#header h1 a {
    color: #ff935e;
}

#header h2 span {
    color: #ffef7c;
}

/* SIDE MENU */
#side-bar .side-block {
    background: #fff0db;
}
#side-bar .heading {
    color: #db6d00;
}
#side-bar div.menu-item.inactive a {
    color: #9b5d36;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #821f1f;
}
#side-bar .collapsible-block-folded {
    background: url(http://scpsandboxcn.wdfiles.com/local--files/other-of-element/default.png) 0 2px no-repeat;
}
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #600;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #600;
}
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #b01;
    text-decoration: none;
}

#page-content .collapsible-block {
        display: inline-block;
	max-width: 100%;
	text-align: center;
	border-radius: 10px;
	border: solid 1px;
	border-color: #bb2d00;
	background-color: #fffde5;
	padding: 10px;
}
.collapsible-block-content p {
	text-align: left;
	padding-left: 5px;
	padding-right: 5px;
}
blockquote {
    border: 1px;
}

.noise {
    position: relative;
    white-space: nowrap;
    color: white;
    text-shadow:
    1px 1px 2px #593116,
    -1px 1px 2px #593116,
    1px -1px 2px #593116,
    -1px -1px 2px #593116;
}

.noise::after {
    content: "尦慺廡婜昞 EGAOer M.V";
    position: absolute;
    left: -0.5px;
    animation: noise-anim 2s infinite linear alternate-reverse;
    white-space: nowrap;
}

.noise::before {
    content: "尦慺廡婜昞 EGAOer M.V";
    position: absolute;
    left: -0.5px;
    animation: noise-anim 1.5s infinite linear;
    white-space: nowrap;
} 

@keyframes noise-anim {
    0% {
        clip-path: inset(40% 1% 61% 0);
    }

    10% {
        clip-path: inset(92% 0 1% 1%);
    }

    20% {
        clip-path: inset(43% 0 1% 0);
    }

    30% {
        clip-path: inset(25% 1% 58% 1%);
    }

    40% {
        clip-path: inset(54% 0 7% 0);
    }

    50% {
        clip-path: inset(58% 1% 65% 0);
    }

    60% {
        clip-path: inset(40% 0 25% 0);
    }

    70% {
        clip-path: inset(94% 1% 57% 1%);
    }

    80% {
        clip-path: inset(8% 0 85% 0);
    }

    90% {
        clip-path: inset(18% 1% 45% 1%);
    }

    100% {
        clip-path: inset(50% 1% 50% 0);
    }
}
[[/module]]


