[[module CSS]]
:root:lang(ko) {
    --header-title: "SCP 재단 대한민국 지역사령부";
    --logo-image: url("http://scpko.wikidot.com/local--files/m48-hub/SCPKO1.png");
    --toggle-roundness: 0%;
    --toggle-icon-color: rgb(244, 244, 244);
    --toggle-border-color: rgb(244, 244, 244);
}
@media only screen and (max-width: 425px) {
:root:lang(ko) {
  --header-title: "SCP 재단 한국사령부";
}
}
::-webkit-scrollbar {
  width: 18px;
  background: #transparent;
}
body::-webkit-scrollbar-track, html::-webkit-scrollbar-track {
  background: rgba(48, 48, 52, 1);
}
body::-webkit-scrollbar-thumb, html::-webkit-scrollbar-thumb {
  background: #cccccc;
  border: #000000 1px solid;
}
body::-webkit-scrollbar-thumb:hover, html::-webkit-scrollbar-thumb:hover {
  background: rgba(244, 244, 244, 1);
}
@media (max-width: 767px) {
body::-webkit-scrollbar, html::-webkit-scrollbar {
  width: 7px;
  height: 0px;
}
}
.authorlink-wrapper {
    --author-top-adjust: -0.7rem;
    --author-bottom-adjust: -0.1rem;
}
[[/module]]
