[[module css]]
html, body {
  overflow-x: hidden;
}

#page-title { display: none; }

.useful-links {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-evenly;
    align-items: center;
    flex-grow: 1;
    margin: 5px -0.5em;
}
.useful-links a {
    margin: 0 10px;
}

.feature-block {
    margin-bottom: 2em;
}
.feature-block,
.feature-block .shadow-panel {
    display: flex;
    flex-wrap: wrap;
    justify-content: flex-start;
    width: 100%;
}
.feature-block .shadow-panel {
    box-shadow: 0 2px 6px rgba(0,0,0,0.5);
    border-radius: 10px;
    margin: 10px 0;
    position: relative;
}

.feature-block .content-panel {
    box-shadow: inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2);
}
.feature-block .shadow-panel .content-panel {
    display: flex;
    align-items: center;
    width: calc(100% - 32px);
    box-sizing: border-box;
    flex: 0 0 auto;
    margin: 0;
    word-break: keep-all;
}
.feature-block .shadow-panel .content-panel.button {
    width: 32px;
    z-index: -1;
    position: relative;
}
.feature-block .button:not(.content-panel) {
    width: 100%;
    text-align: right;
    margin-top: -20px;
}
.feature-block .content-panel.button a.collapsible-block-link {
    margin: 10px;
}
.feature-block .button .collapsible-block-content {
    display: flex !important;
    flex-wrap: wrap;
    width: 100%;
    margin: 5px 0 10px;
}
.feature-block .button .collapsible-block-unfolded {
    display: flex;
    margin-top: 20px;
    justify-content: space-between;
}
.feature-block .button .collapsible-block-unfolded-link {
    order: 2;
    translate: -12px -42px;
    height: 30px;
}
.feature-block .button .collapsible-block-unfolded-link a {
    color: white;
}
.feature-block .button .content-panel {
    translate: 6px;
    margin: 0;
}
.feature-block .button .panel-heading {
    text-align: left;
}
.feature-block .button .panel-body {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: end;
    text-align: left;
    width: 100%;
    box-sizing: border-box;
    background-size: 100% 60%;
}
.feature-block .button .panel-body > p {
    max-width: 500px;
}
.feature-block .collapsible-block-folded a {
    display: inline-block;
    translate: -12px -22px;
    color: white;
}
.feature-block .content-panel p {
    padding: 0;
}
.feature-block .shadow-panel .panel-heading {
    flex-basis: 0;
    min-width: 40px;
    text-align: center;
    padding: 2px 5px;
    text-shadow: 1px 1px 2px rgba(0,0,0,.5);
}
.feature-block .panel-footer {
    font-size: 100%;
    flex-basis: 0;
}
.feature-block .panel-footer p {
    margin: 0; padding: 0;
}
.feature-block .panel-body {
    flex-grow: 1;
    padding: 0.6em 1em;
    border-radius: 10px;
    width: calc(80% - 70px);
    align-self: stretch;
}
.feature-block .shadow-panel .content-panel .panel-body {
    border-radius: 0;
    background-size: 100% 60%;
}
.feature-block .feature-title {
    word-break: keep-all;
    word-wrap: anywhere;
    font-size: 130%;
    font-weight: bold;
}
.feature-block .feature-subtitle {
    font-size: 90%;
}

.feature-block .feature-title > p,
.feature-block .feature-subtitle > p {
    margin: 0;
}

@media (min-width: 1067px) {
  .feature-block .content-panel,
  .feature-block .shadow-panel .content-panel {
      width: calc(50% - 16px);
  }
  .feature-block .shadow-panel .content-panel {
      border-radius: 0;
  }
  .feature-block .shadow-panel .content-panel:first-child {
      border-radius: 10px 0 0 10px;
  }
  .feature-block .shadow-panel .content-panel:last-child {
      border-radius: 0 10px 10px 0;
  }
  .feature-block .mobile,
  .feature-block .shadow-panel .content-panel.mobile {
      display: none;
  }
  .feature-block .button .panel-body {
      height: 100%;
  }
}

@media (max-width: 1066px) {
  .feature-block .wide {
      display: none;
  }
  .feature-block .shadow-panel .content-panel {
      border-radius: 0;
  }
  .feature-block .shadow-panel .content-panel:nth-child(1) .panel-heading p {
      position: absolute;
      top: 50%;
      left: 25px;
      translate: -50% -50%;
  }
  .feature-block .shadow-panel .content-panel:nth-child(3) .panel-heading p {
      display: none;
  }
  .feature-block .shadow-panel .content-panel:nth-child(1) {
      border-radius: 10px 0 0 0;
      border-bottom: none;
      margin-bottom: -1px;
  }
  .feature-block .shadow-panel .content-panel:nth-child(2) {
      border-radius: 0 10px 0 0;
      border-bottom: none;
      margin-bottom: -1px;
  }
  .feature-block .shadow-panel .content-panel:nth-child(3) {
      border-radius: 0 0 0 10px;
      border-top-color: #999990;
      box-shadow: none;
  }
  .feature-block .shadow-panel .content-panel:nth-child(4) {
      border-radius: 0 0 10px 0;
      border-top-color: #999990;
      box-shadow: none;
  }
  .feature-block .shadow-panel .content-panel .panel-body {
      border-radius: 0;
  }
  .feature-block .button .content-panel {
      width: 100%;
  }
  .feature-block .button .feature-title,
  .feature-block .button .feature-subtitle {
      width: 100%;
      text-align: center;
  }
  .feature-block .button .content-panel:first-child {
      border-radius: 10px 10px 0 0;
      margin-bottom: -1px;
  }
  .feature-block .button .content-panel:first-child .panel-body {
      border-radius: 0;
  }
  .feature-block .button .content-panel:last-child,
  .feature-block .button .content-panel:last-child .panel-body {
      border-radius: 0 0 10px 10px;
  }
}

.info-block .panel-body {
    padding: 0;
    border-radius: 0 0 10px 10px;
}

.info-block .info-title {
    font-weight: bold;
    font-size: 115%;
    margin-bottom: 5px;
}

.info-block .info-subtitle {
    font-size: 80%;
