      border-radius: 0 0 10px 0;
      border-top-color: #999990;
      box-shadow: none;
  }
  .feature-block .shadow-panel .content-panel .panel-body {
      border-radius: 0;
  }
  .feature-block .button .content-panel {
      width: 100%;
  }
  .feature-block .button .feature-title,
  .feature-block .button .feature-subtitle {
      width: 100%;
      text-align: center;
  }
  .feature-block .button .content-panel:first-child {
      border-radius: 10px 10px 0 0;
      margin-bottom: -1px;
  }
  .feature-block .button .content-panel:first-child .panel-body {
      border-radius: 0;
  }
  .feature-block .button .content-panel:last-child,
  .feature-block .button .content-panel:last-child .panel-body {
      border-radius: 0 0 10px 10px;
  }
}

