[[module css]]

.bigbox {
   width: 100%;
   height: 100%;
   position: static;
}

/* This Manila folder div was created by JackalRelated and Voiiiii!
*/

div.manilla-background {
    background-color: rgb(241, 213, 146);
    position: relative;
    text-align: center;
    top: 0;
    left: 0;
    z-index: -2;
   }

.manilla-paper {
  width: 80%;
  background-color: white;
  padding: 10px;
  margin: 1em auto;
  text-align: left;
}

.manilla-paper:before,
.manilla-paper:after {
    content: "";
    height: 98%;
    position: absolute;
    width: 80%;
    z-index: -1;
}

.manilla-paper:before {
    background: #f6f6f6;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.2);
    left: 5px;
    top: 3px;
    transform: rotate(-.05deg);
}

.manilla-paper:after {
    background: #fafafa;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
    right: 10px;
    top: 6px;
    transform: rotate(-.4deg);
}

[[/module]]

