[[module CSS]]



div#container-wrap {
    background: url(http://themes.wdfiles.com/local--files/helloworld/background.png) top left repeat-x;
    background-size: 105px;
}

/* HEADER */
#header {
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background: url(https://s1.ax1x.com/2018/11/17/iztH5n.png) 10px 40px no-repeat;
}
#header h1 {
    margin-left: 15%;
    padding: 10px 0px 0px 0px;
    float: left;
    max-height: 90px;
}
#header h2 {
    margin-left: 15%;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: BauhausLTDemi, Arial, sans-serif;
    font-size: 120%;
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
font-family: 'Work Sans';
     content: 'å–µå–µå–µå–µå–µå–µ';
color: #ffffff;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}/* FFFFFF */

#header h2 span::before {
