function randInt(minn, maxn) { 
    switch(arguments.length) {
        case 1: 
            return parseInt(Math.random()*minn+1,10); 
        break; 
        case 2: 
            return parseInt(Math.random()*(maxn-minn+1)+minn,10); 
        break; 
            default: 
                return 0; 
            break; 
    } 
} 

function addNumber(start, id, minad, maxad, speed) {
        var o = document.getElementById(id);
        var i = start;
        var Interval;
        Interval = setInterval(function() {
                var a = randInt(minad, maxad);
                i += 3; 
                o.innerHTML = i.toLocaleString(0.1);
            }, speed);  
        }
</script>
