[[module CSS]]
#side-bar .side-block {
    position:relative;
    z-index:3;
}

#footer{
z-index:3;
position:relative;
}



#page-content{
z-index:3;
position:relative;
}

img.small{
z-index:4;
}


a.avatar-hover div{
    position: relative;
    z-index:4;
}

a.avatar-hover div div{
    position: absolute;
    left: 48px;
    top: 0;
    height: 48px;
    right: 0px;
    width: 100%;
    background-color: white;
    opacity: 0.9;
    filter: alpha(opacity=90);
    z-index:4;
}

a.avatar-hover,  a.avatar-hover:hover {
    background: none;
    background-image: url(http://d3g0gp89917ko0.cloudfront.net/v--edac79f846ba/common--theme/base/images/background/opacity2.png);
    padding: 8px;
    text-decoration: none;
    font-size: 130%;
    color: black;
    font-weight: bold;
    z-index:4;

}


