}


hr + div[style="text-align: right;"] {
    position: relative;
}
hr + div[style="text-align: right;"] .page-rate-widget-box {
    top: calc(-0em - 0px);
    right: 0;
    position: absolute;
}

.page-rate-widget-box {
    border-radius: 0;
    box-shadow: 0 0px 0px 0 rgba(0,0,0,0), 0 0px 0px -0px rgba(0,87,55, 0), 0 0px 0px 0 rgba(0,0,0,0);
}
.page-rate-widget-box .rate-points {
    background-color: #005737 !important;
    background-color: var(--light-accent) !important;
    border-color: #005737;
    border-color: var(--dark-accent);
    border-radius: 0;
}
.page-rate-widget-box .rateup, .page-rate-widget-box .ratedown {
    background-color: #fff;
    border-color: #005737;
    border-color: var(--dark-accent);
}
.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a {
    color: #005737;
    color: var(--dark-accent);
}
.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover {
    color: #fff;
    background-color: #005737;
    background-color: var(--light-accent);
}
.page-rate-widget-box .cancel {
    background-color: #005737;
    background-color: var(--light-accent);
    border-color: #005737;
    border-color: var(--dark-accent);
    border-radius: 0;
}
.page-rate-widget-box .cancel a {
    color: #fff;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0;
    color: #005737;
    color: var(--dark-accent);
    background-color: #005737;
    background-color: var(--light-accent);
}

.rate-box-with-credit-button {

    background-color: var(--light-accent);

    border: 0px solid var(--dark-accent);

    border-radius: 0;

    box-shadow: 0 0px 0px 0 rgba(0,0,0,0), 0 0px 0px 0px rgba(0,87,55, 0), 0 0px 0px 0 rgba(0,0,0,0);

}

.creditButton p a {

