[[include :scp-wiki:theme:bedrock]]
[[include :scp-wiki:theme:skipos]]

[[module CSS]]

:root{
    --desktop-wallpaper: linear-gradient(47deg, rgba(150,95,51,1) 0%, rgba(128,75,54,1) 30%, rgba(105,56,57,1) 60%, rgba(83,36,60,1) 90%);

    --basalt-primary-color: 50, 38, 35;
    --basalt-secondary-color: 39, 25, 25;
    --basalt-tertiary-color: 72, 50, 50;
    --basalt-undertone: 235, 157, 2;
    --basalt-dark-element-color: 235, 157, 2;
    --basalt-sub-text-color: 245, 202, 115;

    --borderColor: #eb9d02;
    --bgBadgeColor: #2c1e1d;

    --header-title: 'ARF-01 Internal Servers';
}

div.skip_window {
    background-color: rgb(var(--basalt-background-color), var(--basalt-UI-opacity));
    box-shadow: var(--window-shadow);
}

/* 
----------- The below code has been stolen from Reception ----------- 
----------- https://scp-wiki.wikidot.com/8000contestaftokrator -----------
*/

#page-title,#breadcrumbs { display: none; }
#page-content {
    padding: 0;
    background: transparent;
    border-radius: 0;
}

div.rate-box-with-credit-button .creditButton>p>a::before {
    border-radius: calc(var(--window-radius)/4);
}

/*--------------------------------------------------*/

.head-container {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
    background-color: rgb(var(--basalt-secondary-color));
    padding: .15rem .875rem;
    gap: .25rem .75rem;
    border-start-start-radius: var(--window-radius);
    border-start-end-radius: var(--window-radius);
}

.head-container .pseudo-title {
    display: grid;
    flex-grow: 1;
    font-family: var(--header-font);
}
.pseudo-title span:first-child {
    display: inline-block;
    padding-block: 0.125rem;
    font-size: 0.675rem;
}
.pseudo-title span:first-child a {
    display: inline-block;
    background-color: rgb(var(--basalt-background-color));
    border-radius: var(--window-radius);
    padding: 0.125em 0.5em;
    transition: background-color 0.125s ease-out;
}
.pseudo-title span:first-child a:is(:hover,:focus) {
    text-decoration: none;
    background-color: rgb(var(--link-color));
    color: rgb(var(--basalt-secondary-color));
}

.pseudo-title span:nth-child(2) {
    font-weight: bold;
    font-size: 1.325rem;
}

.head-container .rate-box-with-credit-button {
    margin: 0;
    box-shadow: none;
}

ul.creditRate + div { display: none; }

.body-container {
    padding: .9125rem;
    position: relative;
    border-end-start-radius: var(--window-radius);
    border-end-end-radius: var(--window-radius);
}
.body-container::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: 100%;
    box-shadow: var(--window-shadow);
    background-color: rgb(var(--basalt-background-color),var(--basalt-UI-opacity));
    -webkit-backdrop-filter: blur(.175rem);
    backdrop-filter: blur(.175rem);
    border-radius:  inherit;
    z-index: -1;
}

.danger-diamond > .quadrants > div {
    background-color: transparent !important;
}

.skip_box.terminal::after{
    content: attr(data-title);
    font-size: min(calc(1.5vw + .25em),1em);
    line-height: 1.25;
}

/*--------------------------------------------------*/

.body-container h3 {
    margin-block-start: 1.5rem;
    border-block-end: 0.05rem solid rgb(var(--basalt-main-text-color),0.25);
    padding-block-end: 0.325rem;
}

/*--------------------------------------------------*/

.box {
    display: grid;
    grid-template-areas:
        "pic title"
        "pic desc"
        "pic desc";
    grid-template-columns: 30% 70%;

    background-color: rgb(var(--blockquote-background-color),var(--basalt-UI-opacity));
    border-radius: 1.5rem;
    border: solid var(--blockquote-border-width) rgb(var(--blockquote-border-color));
    box-shadow: var(--window-shadow);
    margin-bottom: 1em;
}

.pic {
    grid-area: pic;
    display: grid;
    align-self: center;

    padding: 1em;
    transition: transform .30s;
}

.pic:hover {
    transform: scale(1.25);
    z-index: 2;
}
.pic:focus-within {
    transform: scale(1.25);
    z-index: 2;
}

.title {
    display: grid;
    grid-area: title;
    align-self: center;

    text-align: center;
    padding: 0 1em;
}

.title h2 {
    margin-bottom: 0;
}

.desc {
    grid-area: desc;
    display: grid;
    align-self: center;

    padding: 1em;
    text-align: center;
}

.desc p {
    margin-top: 0;
    margin-bottom: 0.5rem;
}

@media only screen and (max-width: 767.98px) {

.box {
    display: grid;
    grid-template-areas:
        "title title"
        "pic pic"
        "desc desc";

    border: solid var(--blockquote-border-width) rgb(var(--blockquote-border-color));
}

.desc {
    border-top: solid var(--blockquote-border-width) rgb(var(--blockquote-border-color));
}

div.skip_window {
    padding: 0 0.5em;
    margin: 1.5rem 0;
}

.skip_window::before {
    padding: 0.625em 1.5rem;
    margin: 1.5rem -0.6em;
}

.yui-navset.yui-navset-top .yui-content {
    padding: 1em 0.5em;
}

}

