    content: "MisterFrown";
    color: #fff;
    font-family: Inter, Arimo, Verdana, Geneva, "Helvetica Neue", "Helvetica", Arial, sans-serif;
    display: block;
    pointer-events: none;
    grid-column: 1;
    grid-row: 1;
}

