@import url('http://scpko.wdfiles.com/local--files/unfont/UnBatang.css');
@font-face {
    font-family: '망고빙수';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2106@1.1/SF_IceMango.woff') format('woff');
