/* タイトル色変更 */
h1, #page-title {
    color: rgb(32, 18, 77);
}

body {
    background: rgb(237, 240, 247);
    color: rgb(32, 18, 77);
    font-family: "Playfair Display", 'Batang', 'UnBatang', serif;
}
a,a:visited {
    color: rgb(32, 18, 77);
}
h1,h2,h3,h4,h5,h6 {
    color: rgb(32, 18, 77);
    font-family: "Playfair Display", 'UnBatang', serif;
}
div#container-wrap {
    background: url(https://scp-jp-storage.wdfiles.com/local--files/file%3A5670836-9-890z/%E5%A4%A9%E3%83%8E%E5%B7%9D.jpg) top left repeat-x;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "PoI-1933";
    color: #eee;
    font-family: 'BauhausLTDemi', '망고빙수', san-serif;
}
div#header h1 a:after {
    content: "연구실";
    color: #eee;
    font-family: 'BauhausLTDemi', '망고빙수', san-serif;
    position: relative;
    bottom: -0.25em;
}

div#side-bar div.side-block {
    background: rgb(231, 243, 253);
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(32, 18, 77);
    border-color: rgb(32, 18, 77);
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: rgb(32, 18, 77)!important;
    border: solid rgb(32, 18, 77) 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid rgb(32, 18, 77)!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: rgb(32, 18, 77)!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}
div.open-menu a {
    color: rgb(32, 18, 77)!important;
    border-color: rgb(32, 18, 77)!important;
}

.rt {
  user-select: none;
}

[[/module]]

[[module CSS]]
#page-content .rate-box-with-credit-button .page-rate-widget-box {
  margin: 0;
}
#page-content .rate-box-with-credit-button .creditButton {
  background-color: rgb(32, 18, 77);
  border: none;
  border-radius: 0;
  box-shadow: none;
}
