[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Poiret+One&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@300&display=swap');

/* ---------------------------------
Just testing shit out
2021 Wikidot Theme
Created by tyumen, ripped off from the JustGirlyThings theme by stormbreath and dianaberry
Header Image: https://www.publicdomainpictures.net/pictures/240000/velka/scenic-landscape-1508373727uru.jpg/
--------------------------------- */

/* -------------- HEADER -------------- */
div#container-wrap {
    background-image: url(none);
}

div#extra-div-1 {
    height: 150px;
    width: 100%;
    top: 0;
    position: absolute;
    background: url("https://www.publicdomainpictures.net/pictures/240000/velka/scenic-landscape-1508373727uru.jpg");
    background-size: cover;
    background-position: 20% 20%;
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: linear-gradient(to bottom,
      hsl(208, 100%, 14%),
      hsl(208, 100%, 7%));
}

#header h1 a {
    color: transparent;
    text-shadow: none;
}

#header h1 a::before {
    content: "Tyumen";
    font-family: 'Poiret One', cursive;
    color: hsl(208, 100%, 10%);
}

#header h2 span {
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: "est. 2017";
    color: hsl(208, 100%, 6%);
    font-family: 'Noto Sans JP', sans-serif;
    text-shadow: 1px; hsla(208, 100%, 36%, 0.5);
    letter-spacing: 2px;
    padding-left: 0.5em;
}

/*-------------- LOG-IN INFO -------------- */
#login-status,
#login-status a {
    color: hsl(208, 100%, 98%);
}

#login-status ul a:hover {
   font-weight: bold;
}

#account-topbutton {
    background: transparent;
    color: hsl(208, 100%, 14%);
    border: none;
}

#account-options {
    background: hsl(208, 90%, 95%);
    border-color: hsl(208, 55%, 55%);
}

/* -------------- SEARCH-BAR -------------- */
#search-top-box-input {
     border: solid 1px #e5e5e5;
     color: #e5e5e5;
     background-color: hsl(208, 100%, 6%);
}

#search-top-box-input:hover,
#search-top-box-input:focus {
     background-color: hsl(208, 90%, 20%);
}

#search-top-box-form input[type=submit] {
     border: solid 1px #e5e5e5;
     color: #e5e5e5;
     background-color: hsl(208, 100%, 6%);
     background: linear-gradient(
       to bottom,
       hsl(208, 90%, 26%),
       hsl(208, 90%, 16%),
       hsl(208, 90%, 6%));
     box-shadow: none;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
     background-color: hsl(335, 90%, 60%);
     background: linear-gradient(
       to bottom,
       hsl(208, 90%, 46%),
       hsl(208, 90%, 36%),
       hsl(208, 90%, 26%));
     box-shadow: none;
}

/* -------------- TOP-BAR -------------- */
#top-bar ul li ul {
    border: solid 1px hsl(208, 90%, 85%);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid hsl(208, 90%, 85%);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: hsl(208, 90%, 95%);
}

/* -------------- BODY -------------- */
body {
     background: hsl(335, 100%, 100%);
     color: hsl(335, 100%, 0%);
}

#page-title, h1 {
    color: hsl(208, 95%, 20%)
}

#page-title {
    border-bottom-color: hsl(208, 90%, 13%);
}

/* -------------- SIDE-BAR -------------- */
#side-bar .side-block {
    border: 1px solid hsl(208, 90%, 25%);
    box-shadow: 0 2px 6px rgba(0, 87, 163, 1);
    color: hsl(208, 90%, 25%);
    background: hsl(208, 100%, 98%);
}

#side-bar .heading {
    color: hsl(208, 90%, 25%);
    border-bottom: solid 1px hsl(208, 90%, 25%);
}

#side-bar .side-block.media {
    background-color: hsl(208, 55%, 55%);
}

#side-bar .side-block.resources {
    background-color: hsl(208, 90%, 95%);
}

.side-block .menu-item > .image,
iframe.scpnet-interwiki-frame {
    filter: hue-rotate(340deg) brightness(130%);    
    -webkit-filter: hue-rotate(340deg) brightness(130%);
}

/* -------------- FOOTER -------------- */
#footer {
    background: hsl(208, 100%, 13%);
    color: hsl(208, 100%, 55%);
}

/* -------------- LINKS -------------- */
a,
#side-bar a:visited,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover,
a.newpage {
    color: hsl(208, 95%, 35%);
}

a:visited {
    color: hsl(208, 95%, 35%);
}

/* -------------- PAGE EDITING OPTIONS -------------- */
.buttons .btn {
    background-color: hsl(208, 90%, 95%);
    border-color: hsl(208, 90%, 85%);
    color: hsl(208, 100%, 14%);
}

.buttons .btn:hover {
    background-color: hsl(208, 90%, 90%);
    color: hsl(208, 100%, 14%);
}

#lock-info {
    background-color: hsl(208, 90%, 95%);
    border-color: hsl(208, 90%, 85%);
}

/* -------------- RATING MODULE -------------- */
.page-rate-widget-box .rate-points {
    background-color: hsl(335, 55%, 55%) !important;
    color: hsl(335, 90%, 95%) !important;
    border: solid 1px hsl(335, 55%, 55%);
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: hsl(335, 90%, 95%);
    border-top: solid 1px hsl(335, 55%, 55%);
    border-bottom: solid 1px hsl(335, 55%, 55%);
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: hsl(335, 55%, 55%);
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: hsl(335, 55%, 55%);
    color: hsl(335, 90%, 95%);
}
.page-rate-widget-box .cancel {
    background: transparent;
    background-color: hsl(335, 55%, 55%);
    border: solid 1px hsl(335, 55%, 55%);
}
.page-rate-widget-box .cancel a {
    color: hsl(335, 90%, 95%);
}
.page-rate-widget-box .cancel a:hover {
    background: hsl(335, 90%, 95%);
    color: hsl(335, 55%, 55%);
}

/* Info Module */
#page-content .rate-box-with-credit-button {
    background-color: hsl(208, 55%, 55%);
    border-color: hsl(208, 55%, 55%);
    border-radius: 5px;
    box-shadow: 1px 1px 3px rgba(0,0,0,.5);
}
#page-content .creditButton p a {
    border-left-color: hsl(335, 90%, 95%);
}

#page-content .creditButton p a:hover {
    color: hsl(305, 100%, 15%);
}

/* -------------- OTHER ELEMENTS -------------- */
blockquote,
div.blockquote,
.code {
    border-color: hsl(208, 90%, 6%);
    background-color: hsl(208, 90%, 90%);
}

#page-content .wiki-content-table tr th {
     background-color: hsl(208, 100%, 55%);
     color: hsl(208, 100%, 30%);
     border: 1px solid hsl(208, 100%, 14%);
}

.scp-image-block .scp-image-caption {
    background-color: hsl(208, 90%, 90%);
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: transparent !important; /* tab background */
    border-color: transparent;
    color: hsl(208, 100%, 90%);
}

.yui-navset .yui-nav a em {
     background-color: hsl(208, 100%, 90%);
     box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
     border-color: hsl(208, 90%, 15%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-image: none;
    background-color: hsl(208, 100%, 15%); /* selected tab background */
    color: hsl(208, 100%, 20%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: hsl(208, 100%, 55%);
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    background-color: hsl(208, 100%, 55%);
}

.yui-navset .yui-content {
    background-color: hsl(208, 100%, 90%); /* content background color */
}

/* -------------- MOBILE VIEW -------------- */
@media (max-width: 767px) {
    #side-bar {
          background-color: hsl(208, 100%, 25%);
     }

    #top-bar .open-menu a {
         border: 0.2em solid hsl(208, 100%, 25%);
         background-color: hsl(208, 100%, 34%);
         color: hsl(208, 100%, 25%);
    }
}
[[/module]]

