[[module CSS]]
/*
    Queen's Gambit CSS Theme
    [2022 Wikidot Theme]

    Created by c_bonefish
    Base Code by Denevola

    Background Image1: https://pixabay.com/vectors/architecture-building-castle-home-1298980/
    Background Image2: https://pixabay.com/vectors/flourish-frame-decorative-2858823/

    CC BY-SA 3.0
*/

/* Header */
div#container-wrap {
    background: url('https://scp-wiki.wikidot.com/local--files/theme:queen-s-gambit/blackqueen-background.png') center top repeat-x;
    background-size: 114px;
}

#header {
    height: 240px;
    background-image: url('https://scp-wiki.wikidot.com/local--files/theme:queen-s-gambit/blackqueen-logo.png');
    background-position: 50% 40%;
    background-repeat: no-repeat;
    background-size: 200px auto;
}

#header h1 a {
    position: absolute;
    font-size: 100%;
    text-align: center;
    text-shadow: none;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
}

#header h1 a::before {
    content: " ";
    color: #00000000;
    width: 100%;
    height: 100%;
}

#header h2,
#header h1 a span,
#header h2 span {
    display: none;
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 3px;
    color: #FCFBF9;
    z-index: 2;
}

#login-status a {
    color: #FCFBF9;
    font-weight: bolder;
}

#account-topbutton {
    border-color: #FCFBF9;
}

#login-status ul a {
    color: #000000 !important;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: #FCFBF9;
    border: solid 1px #000;
    color: #000;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: #000000 !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #000;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #FCFBF9;
    color: #FCFBF9;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #000;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 232px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #FCFBF9;
    background: #00000044;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #A57947;
    border-color: transparent;
    color: #FCFBF9;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #000;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #D5D5D5;
}

/* Page Elements */
#page-title,
.meta-title {
    color: #484848;
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #484848;
    font-weight: bold;
}

body {
    background-color: #F2F2F2;
    color: #000000;
}

a:link {
    color: #73612E;
}

a:visited {
    color: #867036;
}

.hovertip {
    background-color: #F6F6F6 !important;
    border: 2px solid #666666 !important;
    color: #000000 !important;
}

.scp-image-block {
    background: #FAFAFA;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
}

.scp-image-block .scp-image-caption {
    background: #E4E4E4;
    border-top: none;
    font-size: 85%;
}

.scp-image-block img {
    box-shadow: 0 0 1px black;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #F2F2F2;
}

::-webkit-scrollbar-thumb {
    background: #000;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #545454;
}

textarea::-webkit-scrollbar-track {
    background: #F2F2F2;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block.media,
#side-bar .side-block.resources,
#interwiki .side-block {
    background-color: #F2F2F2;
    border: 2px solid #C4A44F;
    border-radius: 10px 0;
}

#side-bar .heading,
#interwiki .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #C4A44F;
    color: #73612E;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #867036;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #73612E;
}

#side-bar .menu-item .image,
#interwiki .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: grayscale(80%);
}

#side-bar .collapsible-block-folded a:link {
    filter: grayscale(80%);
}

#side-bar a:link,
#interwiki a:link {
    color: #73612E;
}

#side-bar a:visited,
#interwiki a:visited {
    color: #867036;
}

#side-bar a:link:hover,
#side-bar a:visited:hover,
#interwiki a:link:hover,
#interwiki a:visited:hover {
    color: #C4A44F;
}

#top-bar .open-menu a {
    border-color: #C4A44F;
    color: #000000;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    border: 1px solid gainsboro;
    border-bottom: none;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background: none;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #9C9C9C;
    color: #000;
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: #F6F6F6;
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    background-color: #000;
    border: 2px solid #C4A44F;
    border-radius: 0px;
    box-shadow: none;
}

.page-rate-widget-box > span {
    border: none !important;
}

div.page-rate-widget-box .rate-points {
    background: #0F0F0F;
    color: #fff;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #0F0F0F;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #fff;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #C4A44F;
    color: #0F0F0F;
}

.page-rate-widget-box .cancel {
    background: #0F0F0F;
}

.page-rate-widget-box .cancel a {
    color: #fff;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #C4A44F;
    border-radius: 0;
    color: #0F0F0F;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #000 !important;
    border: 2px solid #C4A44F !important;
    border-radius: 0px !important;
    box-shadow: none !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: none !important;
    color: #fff !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #C4A44F;
    border-radius: 0 3px 3px 0;
    color: #0F0F0F !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #ffffff;
    border-color: #8C8C8C;
}

.owindow .modal-header {
    background-color: #ffffff;
}

.owindow .modal-body img {
    background-color: #ffffff !important;
    color: #000000;
}

.owindow .title {
    background-color: #ffffff;
}

.owindow a,
.owindow a:visited {
    color: #CECECE;
}

.owindow .active {
    color: #000000;
}

.owindow .content.modal-body img {
    background-color: #727272 !important;
}

.owindow .button-bar a {
    background-color: #ffffff;
    color: #000000;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #ffffff !important;
    border-color: #999999;
    color: #000000;
}

table.page-files .highlight {
    background-color: #FBFBFB;
}

input.text,
input.text:focus,
input.text:hover {
    background-color: #ffffff !important;
    border-color: #999999;
    color: #000000;
}

textarea {
    background-color: #FEFEFE;
    color: #000000;
}

.change-textarea-size a {
    background-color: #ffffff;
    color: #000000;
}

#lock-info {
    background-color: #ffffff;}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #FCFBF9;
    border-style: dotted;
}

/* etc. */
@media (min-width: 581px) and (max-width: 767px) {
    #header {
        background-size: 200px auto;
    }
}

@media (min-width: 480px) and (max-width: 580px) {
    #header {
        background-size: 180px auto;
    }
}

@media (min-width: 321px) and (max-width: 479px) {
    #header {
        background-size: 170px auto;
    }
}

@media (max-width: 320px) {
    #header {
        background-size: 150px auto;
    }
}
