[[module css]]
@import url('https://fonts.googleapis.com/css?family=ZCOOL+KuaiLe&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Balsamiq+Sans&display=swap');

.rmpostcontainer {
    background-image: url('http://scp-wiki.wikidot.com/local--files/component:laruepost/LaRueDesk.png');
    background-repeat: no-repeat;
    width: 100%;
    max-width: 660px;
    height: 980px;
    margin: 0px;
    padding: 60px 20px 0px 60px;
}
.rmpostcontent {
    font-family: 'ZCOOL KuaiLe', cursive;
    font-style: normal;
    font-size: 1.1em;
    color: #000;
    width: 530px;
    height: 880px;
    padding: 30px;
    margin: -8px 0px 0px 0px;
}
.rmpostmobiletitle {
    display: none;
}
.scribble {
    font-size: 1.30em;
    color: #000;
    width: 300px;
    padding: 0px 0px 0px 20px;
    margin: -8px 0px 0px 0px;
    -ms-transform: rotate(20deg);
    transform: rotate(12deg);
}
@media only screen and (max-width: 600px) {
    .rmpostcontainer {
        background-image: url("http://scp-wiki.wikidot.com/local--files/component:laruepost/LaRueMobile.png");
        background-size: 100%;
        background-repeat: repeat;
        width: 100%;
        height: auto;
        padding: 0px;
    }
    .rmpostcontent {
        font-size: 14pt;
        width: 85%;
        height: auto;
        padding: 10px 10px 0px 30px;
    }
    .rmposttitle {
        width: auto;
        height: auto;
        padding: 0px 20px 0px 70px;
    }
    .scribble {
        display: none;
    }
}

.image-container.aligncenter {
    padding-top: 0;
    padding-bottom: 0;
}

#page-content .blockquote {
    background-color: #333333;
}
#page-content .modalbox {
    background: #333333;
}

.inline-diff del,
.inline-diff ins {
    color: #000;
}

/* Sidebox by EstrellaYoshte */
#page-content .sidebox tr td,
#page-content .sidebox tr th {
    padding: .35em;
}
#page-content .authorbox tr td,
#page-content .authorbox tr th {
    padding: .35em;
}
.limit{
    margin-bottom: -1rem;
}
.anchor{
    position: sticky;
    height: 0;
    top: 0;
}
.authorbox,
.sidebox {
    position: absolute;
    width: calc((100vw - 870px) / 2);
    max-height: calc(100vh - 18rem);
    margin: 0 8px 0 0;
    padding: .14rem;
    box-sizing: border-box;
    overflow: auto;
    z-index: 5;
}
.authorbox {
    max-width: calc((100vw - 921.2px) / 2) !important;
    top: .5em;
    right: 103.5%;
}
.sidebox{
    top: 0;
    left: 103.5%;
    padding-left: 1em;
}

@media (max-width:1079px) {
  .authorbox,
  .sidebox {
      display: none;
  }
}
[[/module]]

