[[module CSS]]
/* FONTS */
@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@500;700&display=swap');

/* AESTHETIC */
body {
    font: 0.95em 'Poppins', sans-serif;
    background: #1a1a1a;
    background: linear-gradient(90deg, rgba(0,0,0,1) 0%, rgba(26,26,26,1) 5%, rgba(26,26,26,1) 95%, rgba(0,0,0,1) 100%);
}
.page-rate-widget-box {
    font: .95em verdana,arial,helvetica,sans-serif;
}
div#container-wrap {
    background-image: none;
}
#top-bar {
    background: rgb(83,83,83);
    background: radial-gradient(circle, rgba(83,83,83,1) 0%, rgba(59,59,59,1) 100%);
    margin: 1em 0;
    padding-bottom: .2em;
}
#top-bar ul li a,
#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    border: 0;
}
#page-title {
    display: none;
}
#breadcrumbs {
    margin: 0;
}
[[/module]]

