@import url('https://fonts.googleapis.com/css?family=Muli:700');

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: 'Muli', sans-serif;
    font-weight: 700;
    font-size: 180%;
    text-decoration: none;
    text-shadow: none;
    letter-spacing: 0.9px;
}

#header h1 a::before {
    content: "Natalie Watts ";
    color: #eee;
    text-shadow: 3px 3px 5px #000;
}

@media only screen and (max-width: 640px) {
  #header h1 a {
    font-size: 90%;
  }
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none}

#header h2 span::before {
    content: "we make our own luck. ";
    color: #eee;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

/* YUI-TABS */
.yui-navset .yui-content {
    background-color: inherit;
}
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: inherit;
    background-image: inherit;
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus,
/* no focus effect for selected */
 .yui-navset .yui-nav .selected a:hover {
    /* no hover effect for selected */
    background: inherit;
    /* selected tab background */
    color: inherit;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: inherit;
    text-decoration: inherit;
}
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav,.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: inherit;
}
.yui-navset li {
    line-height: inherit;
}

.yui-navset * {
    transition: background-color 80ms cubic-bezier(0.4, 0.0, 0.2, 1), color 80ms cubic-bezier(0.4, 0.0, 0.2, 1);
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    zoom: unset;
    display: flex;
    flex-wrap: wrap;
    border-color: rgb(140, 20, 25);
    width: calc(100% - 0.125rem);
    margin: 0 auto;
    border: 0;
    box-shadow: 0 calc(0.0625rem * 5) 0 0 rgb(140, 20, 25);
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    zoom: unset;
    background-image: none;
    background-color: rgb(244, 244, 244);
    border: unset;
    color: rgb(12, 12, 12);
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    zoom: unset;
    background-color: rgb(100, 3, 15);
    color: rgb(252, 252, 252);
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
    background-color: rgb(244, 244, 244);
    border-color: transparent;
    max-width: 100%;
    padding: 0;
    margin: 0;
    zoom: unset;
    display: flex;
    position: relative;
    flex-grow: 2;
    box-shadow: 0 0 0 0.0625rem rgb(140, 20, 25);
}

.yui-navset .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset-bottom .yui-nav li a {
    display: flex;
    vertical-align: bottom;
    zoom: unset;
    width: 100%;
    align-items: center;
    justify-content: center;
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
    padding: 0.35em 0.75em;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
}

.yui-navset .yui-nav li em {
    /* Undoes border set by wikidot's default layout */
    border: unset !important;
}

/* Selected Tab */

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected {
    padding: 0;
    margin: 0;
    flex-grow: 2;
    background-color: rgb(140, 20, 25);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border: none;
    border-color: transparent;

}

.yui-navset .yui-nav .selected a {
    /* Undoes a color set by wikidot's default layout */
    color: rgb(252, 252, 252) !important;
    width: 100%;
    background-image: none;
}

.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:active {
    background-color: rgb(140, 20, 25);
    color: rgb(252, 252, 252);
}

.yui-navset .yui-nav .selected a:hover {
    /* Indicate that the currently selected tab is not clickable */
    cursor: default;
}


.yui-navset .yui-content {
    background-color: rgba(244, 244, 244, 0.25);
    /* content background color */
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
    border-color: rgb(170, 170, 170);
    /* content border */
}

.yui-navset-left .yui-content {
    border-left-color: rgb(244, 244, 244);
    /* different border color */
}

