[[code type="CSS"]]
@font-face {
    font-family: 'GmarketSans';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2001@1.1/GmarketSansMedium.woff') format('woff');
    font-weight: normal;
    font-style: normal;
}
:root:lang(ko) {
    --bg-color: 255, 255, 255;
    --top-bar-position: sticky;
    --header-height: 50px;
    --top-bar-height: 50px;
    --title-align: left;
    --left: 1;
    --title-font: 'Work Sans', 'GmarketSans', sans-serif;
    --h1-top: -7px;
    --h1-size: 21px;
    --alt-title-padding: 55px;
    --hlt-opa-h1: 0;
    --tob-bar-justify: start;
}
#header h1, #header h2 {
    margin: 0;
}
div#header-extra-div-1 {
    opacity: 1;
    background-position: top 6px left 10px;
    background-size: calc(50px/1.2);
    margin: 0;
}
div#header-extra-div-2 {
    background: linear-gradient(rgba(var(--bg-color),0.9), rgba(var(--bg-color),0.5));
    backdrop-filter: blur(2px);
    -webkit-backdrop-filter: blur(2px);
}
div#header-extra-div-3 {
    box-shadow: 0 2px 10px rgba(0,0,0,.3);
}
div#extra-div-4 {
    background: none;
}
#content-wrap {
    margin: calc(var(--header-height) - 20px) auto var(--header-height) auto;
}
#top-bar {
    margin: 0;
    left: 185px;
}
.mobile-top-bar {
    justify-content: center;
}

@media all and (max-width: 767px) {
:root:lang(ko) {
    --header-height: 100px;
    --top-bar-height: 50px;
}
div#header-extra-div-2 {
    box-shadow: 0 2px 10px rgba(0,0,0,.3);
}
div#header-extra-div-3 {
    box-shadow: none;
}
#top-bar {
    left: 0;
    justify-content: center;
}
}
