[[module CSS]]

/* 页眉 */
div#container-wrap {
    background: #000 repeat-x;
    height: 161px;
}

#header {
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */

background: url(http://scpsandboxcn.wikidot.com/local--files/day-by-die/goi-2) no-repeat left 30px !important;
background-size:100px 100px !important;
max-width: none;
width:50%;
max-width: none;
right: 0;
left: 0;

width:100%;
max-width: 980px;
margin: 0 auto;
}

#header h1 a {
    color: transparent;
    background: transparent;
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
    font-family: 'Work Sans';
    content: 'ㅤ非穷举众议联合';
    color: #DCDCDC;
}

#header h2 span {
    display: block;
    color: transparent;
}

#header h2 span::before {
    content: '[基于非穷举筛查检索引擎]';
    color: #ffffff;
}

/* 顶边栏 */
#top-bar ul li:hover a:link{
    background: #f5f5f5;
    color: #000
}

#top-bar ul li:hover a:visited{
    background: #f5f5f5;
    color: #000
}

#top-bar ul li:hover a:hover {
    background: #000;
    text-decoration: none;
    color:#f5f5f5;
}

/* 左边栏 */ 
#side-bar a, #side-bar a:visited {
    color: #000000;
}

#side-bar a:hover {
    color: #fff;
    text-decoration: underline;
    background: #000;
}

#side-bar .heading {
    color: #000000;
    border-bottom: solid 1px #000000;
}

#side-bar div.menu-item.inactive a {
    color: #000000;
}
#side-bar div.menu-item .sub-text {
    font-size: 80%;
    color: #000000;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000000;
}

#side-bar .side-block {
    padding: 10px;
    border: 1px solid #000000;
    border-radius: 0px;
    background: #A9A9A9 !important; 
    margin-bottom: 15px;
}

/* 通用 */
body{
    color: #000;
}
#content-wrap {
    position: relative;
    margin: 2em auto 0;
    max-width: 1040px;
    min-height:1300px;
    height: auto !important;
    height: 1500px;
}
h1, #page-title {
    color: #000000;
    padding: 0 0 0.25em;
    margin: 0 0 0.6em;
}
h1 {
    margin-top: 0.7em;
    padding: 0;
    font-style: normal;
    font-weight: lighter;
}
h2, h3, h4, h5, h6, h7 {
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
}
#page-title {
    font-weight: lighter;
    border-color: #ffffff;
}

ul {
    /*list-style-image: url(../images/bullet.gif);*/
    list-style: square;
}
#top-bar ul {
/* list-style-image: none; /* IE ONLY! IF list-style-image IS SET */
}

li, p {
    line-height: 141%;
}

/* 选项卡 tab格式 */
/* 所有选项卡的父格式  */
.yui-navset .yui-content{
    background-color: #c0c0c0;
    border-width: 1px 1px 1px 1px;
    border-color: #000;
}

/* 没有选中的选项卡 */
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: #fff;
    color: #000;
    border-width: 1px 1px 0px 1px;
}

/* 正被选中的选项卡 */
.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: #000;
    color: #fff;
    border-width: 1px 1px 0px 1px;
}

/* 鼠标扫过时，选项卡属性 */
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #c0c0c0;
    color: #000;
    border-width: 1px 1px 0px 1px;
}

/* 用于删除选项卡周围原版式的边框 */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-width: 0px 0px 5px 0px;
    border-color: #000;
}

/* 评分模组格式 */
.page-rate-widget-box .rate-points {
    background-color: #000 !important;
    border: solid 1px #000000;
    border-right: 0;
    border-radius: 0;
    color: #ffffff;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #000000;
    border-top: solid 1px #000000;
    border-bottom: solid 1px #000000;
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #ffffff;
    padding: 0 4px;
    margin: 0 1px;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #ffffff;
    color: #000000;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: #000;
    border: solid 1px #000000;
    border-left: 0;
    border-radius: 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #ffffff;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0;
    background: #ffffff;
    color: #000000;
    text-decoration: none;
}

/* 著作信息模块格式 */

#u-credit-view-button {
    display: block;
    text-align: right;
}

.rate-box-with-credit-button{
    background-color: #000 !important;
    color: #fff !important;
    border: solid 1px #000 !important;
    border-radius: 0 !important;
}
.rate-box-with-credit-button .creditButton p a{
    border-left: solid 1px #000 !important;
    border-radius: 0 !important;
}
.rate-box-with-credit-button .creditButton p a:hover {
    background-color: #fff !important;
    color: #000 !important;
}
.rate-box-with-credit-button .page-rate-widget-box .cancel {
    border-radius: 0;
    border-bottom: 0;
}
.rate-box-with-credit-button .page-rate-widget-box .cancel a:hover {
    border-radius: 0;
}
.rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
    border-bottom: 0;
}
.rate-box-with-credit-button .page-rate-widget-box .rateup,
.rate-box-with-credit-button .page-rate-widget-box .ratedown {
    border-bottom: 0;
}
.creditButtonStandalone p a{
    background: #000 !important;
    color: #fff !important;
    border-radius: 0 !important;
}
.creditButtonStandalone p a:hover {
    background: #fff !important;
    color: #000 !important;
}
.modalbox {
    border: #000 solid 1px !important;
}

/* 搜索栏 */
#search-top-box-input {
    color: #000;
    background-color: #e0e0e0;
    border-radius: 0 0 0 0;
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    color: #000;
    background-color: #fff;
    border-radius: 0 0 0 0;
}
#search-top-box-form input[type=submit] {
    border-color: #000;
    color: #000;
    border-radius: 0 0 0 0;
    background-color: #A9A9A9;
    background: linear-gradient(to bottom, #A9A9A9, #A9A9A9);
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border-color: #000;
    color: #000;
    border-radius: 0 0 0 0;
    background-color: #A9A9A9;
    background: linear-gradient(to bottom, #A9A9A9, #A9A9A9);
}

/*链接选项*/
a {color: #005b93;  text-decoration: none;  background: transparent;}
a:link{color: #005b93;  text-decoration: none;  background: transparent;}
a:newpage{color: #005b93;  text-decoration: none;  background: transparent;}
a:visited{color: #005b93;  text-decoration: none;  background: transparent;}
a:hover{color: #005b93;  text-decoration: underline;  background: #005b93;}

}

.email {float: center; border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px 10px 10px 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)}
a.collapsible-block-link { font-weight: bold; }

.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px #000098}

@import url('https://fonts.googleapis.com/css?family=Special+Elite');

.manifestoPaper {
    color: #351f11;
    font-family: Courier;
    font-size: 14px;    background: url('http://www.rpc-wiki.net/local--files/malthusian-manifesto/PaperTexture.jpg');
    background-repeat: no-repeat;
    background-size: cover;
    box-shadow: 4px 6px #dbdbd9;
    padding: 10px;
    font-family: 'Special Elite', cursive;
}

