[[module css]]
#page-content img {
    max-height:100vh;
}

#header {
    height: auto;
}

#header h1,
#header h2,
#search-top-box {
    display: none;
}

#page-title {
    display: none;
}

div#container-wrap {
    background: none;
}

#top-bar {
    top: 3em;
    left: 1em;
    right: auto;
}

#top-bar a {
    color: rgb(200, 200, 200);
}

#top-bar ul li ul {
    box-shadow: none;
}

#top-bar ul li a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border: none;
}

#content-wrap {
    margin: 4em auto 12em;
}

.page-rate-widget-box,
.rate-box-with-credit-button .creditButton {
    box-shadow: none !important;
}

.creditRate {
    margin-right: 1em;
    filter: grayscale(1);
}
[[/module]]

[[module CSS]]
#page-content {
      font-family: 'ComicNeueAngularRegular', 'MS Comic Sans', 'Comic Sans', sans-serif;
}

div[class*="award"] + .creditRate .creditButton .fa.fa-info:before {
display: none;
}
div[class*="award"] + .creditRate .creditButton .fa.fa-info:after{
content: "\f091";
color: white!important;
}

[[/module]]
