[[/module]]
[[module css]]
#header {
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background: url( no ) 10px 40px no-repeat;
}

#header h1 {
    margin-left: 20%;
    padding: 10px 0px 0px 0px;
    float: left;
    max-height: 90px;
}
#header h2 {
    margin-left: 24%;
    padding: 0;
    clear: left;
    float: left;
    font-size: 120%;
    max-height: 38px;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-family: BauhausLTDemi, Arial, sans-serif;
    font-size: 120%;
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
    font-family: 'Work Sans';
    content: 'Just Art Work';
    color: #FFF;
    text-shadow: 0 0 5px #f0f, 0 0 1px #00f, 0 0 1px #f00, 0 0 1px #00f,
    0 0 5px #0ff, 0 0 10px #0ff, 0 0 1px #f0f, 0 0 1px #fff;
    font-size: 1.2em;
    font-weight: 400;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '死者的遗留';
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #ffffff;
    text-shadow: 0 0 5px #f0f, 0 0 1px #00f, 0 0 1px #f00, 0 0 1px #00f,
    0 0 5px #0ff, 0 0 10px #0ff, 0 0 1px #f0f, 0 0 1px #fff;
}
[[/module]]

