[[module CSS]]
.wiki-content-table td:nth-last-child(1){
    width: 3ch;
}
.wiki-content-table td:nth-last-child(2){
    width: 12ch;
}
.content-panel .content-type-title > p {
    margin: 0;
}
.content-panel .content-type-title {
    /*font-size: 120%;*/
    font-weight: bold;
    padding: 2px 10px;
    background-color: #666;
    color: #fff;
    border-radius: 9px 9px 0 0;
    box-shadow: inset 0 1px rgba(255,255,255,0.3),
                inset 0 10px rgba(255,255,255,0.2),
                inset 0 10px 20px rgba(255,255,255,0.25);
}
.content-panel .content-type-description {
    padding: 0;
    text-align: justify;
}

.content-panel .wiki-content-table {
    margin: 2px 0 0 0;
    width: 100%;
    word-break: break-word;
}
.content-panel .wiki-content-table th:nth-of-type(2),
.content-panel .wiki-content-table th:nth-of-type(3),
.content-panel .wiki-content-table td:nth-of-type(2),
.content-panel .wiki-content-table td:nth-of-type(3) {
    text-align: center;
}
.content-panel .wiki-content-table th,
.content-panel .wiki-content-table td {
    border: none;
}
.content-panel .wiki-content-table tr {
    border-bottom: solid 1px rgba(0,0,0,0.1);
}
.content-panel .wiki-content-table th {
    border: 2px solid white;
    border-width: 0 2px;
    background-color: #666;
    color: white;
}
.desktop-only { display: block; }
.mobile-only { display: none; }
@media (max-width: 979px) {
    .desktop-only { display: none; }
    .mobile-only { display: block; }
}




.contest-link {
    border: 2px solid white;
    background-color: #666;
    padding: 0.25rem;
    height: auto;
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    text-align: center;
    -webkit-border-radius: 10px;
       -moz-border-radius: 10px;
            border-radius: 10px;
}

.contest-link > a {
    color: white;
    font-weight: bold;
    -webkit-border-radius: 10px;
       -moz-border-radius: 10px;
            border-radius: 10px;
}

.content-panel .panel-body {
    position: relative;
    border-radius: 0 0 10px 10px;
}

.panel-body > div:nth-child(1) > p {
    display: -ms-grid;
    display: grid;
    -ms-grid-columns: 1fr 1px 1fr 1px 1fr;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 1px;
    grid-auto-rows: auto;
    height: 100%;
    margin-top: 0;
    background-color: rgba(0, 0, 0, 0.1);
}

.panel-body > div:nth-child(1) > p > a {
    display: -webkit-box;
    display: -webkit-flex;
    display: -moz-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: center;
    -webkit-align-items: center;
       -moz-box-align: center;
        -ms-flex-align: center;
            align-items: center;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
       -moz-box-pack: center;
        -ms-flex-pack: center;
            justify-content: center;
    width: 100%;
    height: 100%;
    padding: 0.25em;
    text-align: center;
    background: #fff9f0 url(http://www.scp-wiki.net/local--files/component:theme/panel-bg-gradient-reverse.png) bottom repeat-x;
    word-break: inherit !important;
}

.panel-body > div:nth-child(1) br {
    display: none;
}

[[/module]]
