[[include :scp-wiki-cn:theme:parallel bedrock=*]]
[[module CSS]]
/* 标题图生成规范：文楷Bold 40pt，长宽各加5px，描边1px */

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@300;400;700;900&display=swap');

:root {
    --logo: url("https://ah-sandbox.wikidot.com/local--files/newbee/Newbee_Logo.svg");
    --dog: url("https://ah-sandbox.wdfiles.com/local--files/newbee/KC_Dog_Light.svg");

    --title-size: 1.25rem;
    --subtitle-size: 0rem;
    --user-button-width: 2.25rem;
    --side-bar-button-width: 2.25rem;
    --main-content-top-margin: 8rem;
    --main-content-width: 52rem;

    --base-font-size: 1.1rem;

    --body-font: "Noto Sans SC", sans-serif;

    --top-bar-link-color: var(--basalt-main-text-color);
    --header-title-color: var(--basalt-main-text-color);
    --side-bar-button-color: var(--basalt-main-text-color);
    --search-icon-color: var(--basalt-main-text-color);

    --header-border-width: 0rem;
    --blockquote-border-width: var(--base-border-width);

    --main-content-width: 500rem;
}

textarea#edit-page-textarea {
    font-family: var(--mono-font);
}

div#page-content li, div#page-content p, div#page-content div, div#page-content span {
    line-height: 175%;
}

div#page-content p {
    margin-block: 1.5em;
}

div#page-content span.ruby {
    line-height: 1;
    height: unset;
    display: inline-flex;
    flex-direction: column-reverse;
    align-items: center;
    vertical-align: top;
    text-indent: 0;
}

div#page-content span.rt {
    display: block;
    margin-top: -0.625em;
    margin-bottom: 0;
    line-height: 1.25;
}

div#content-wrap {
    position: relative;
    overflow-x: hidden;
}

div#content-wrap::before {
    content: "";
    display: block;
    position: static;
    width: auto;
    height: 22rem;
    margin-top: calc(var(--header-final-height) * -1);
    background-image: linear-gradient(to top, rgba(0, 0, 0, 0.05) 0%, transparent 0.4rem), url("https://ah-sandbox.wdfiles.com/local--files/newbee/KC_Dog.svg");
    background-size: cover, 20rem auto;
    background-position: center;
    background-repeat: repeat-x, repeat;
    background-attachment: scroll, fixed;
}

div#content-wrap::after {
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 25rem;
    left: 0;
    top: 5rem;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/newbee/Newbee_Title.svg");
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
}

div#header h1 a::before {
    margin-right: 0rem;
    mask-image: none;
    webkit-mask-image: none;
}

div#header h1 a span::after {
    margin-bottom: 0;
}

div#header {
    background-image: none;
    background-color: rgb(var(--basalt-primary-color));
}

div#main-content {
    --header-font: "Noto Serif SC", serif;
}

div#page-title {
    display: none;
}

h1 {
    color: inherit;
    font-family: "Noto Serif SC", "Noto Serif TC", serif;
    font-weight: 900;
    font-size: 1.8em;
    text-align: center;
}

div.blockquote {
    background-image: var(--dog);
    background-size: 20rem auto;
    border-radius: 1rem;
    max-width: unset !important;
    box-shadow: 0 0 0.25rem rgba(0, 0, 0, 0.2);
}

div.blockquote.info {
    background-image: none;
    --general-border-color: var(--basalt-tertiary-color);
}

div.blockquote.info li {
    margin-bottom: 1em;
}

div.blockquote.info ul ul,
div.blockquote.info ul ol {
    margin-top: 1em;
}

div.blockquote.pass {
    margin: 0;
    padding: 1.25rem;
    background-image: none;
    box-shadow: 0 0 0.25rem rgba(0, 0, 0, 0.2);
}

div#page-content div.blockquote.pass p {
    margin-block: 0;
}

div.blockquote img.TT {
    margin-top: -1.75rem;
}

div.table {
    overflow-x: auto;
}

div.table table.wiki-content-table {
    margin-block: 0;
}

div.narrow {
    width: auto;
    max-width: 52rem !important;
    margin-inline: auto;
}

div.wide {
    width: auto;
    max-width: 72rem !important;
    margin-inline: auto;
}

div#page-content div.high p {
    line-height: 200%;
}

div.gcontainer {
    display: grid;
    grid-template-areas: "a1 a1 a2" "b2 b1 b1" "c1 c1 c2" "d2 d1 d1";
    grid-template-columns: 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
    row-gap: 3rem;
}

div.passport {
    display: grid;
    grid-template-areas: "a n1 n1"
                         "a n2 n2"
                         "a n3 n3"
                         "n4 n4 b"
                         "n5 n5 b"
                         "n6 n6 b"
                         "c n7 n7"
                         "c n8 n8"
                         "c n9 n9"
                         "c nX nX";
    grid-template-columns: 1fr minmax(20vw, auto) 1fr;
    grid-template-rows: repeat(10, 1fr);
    row-gap: 1rem;
    column-gap: 2rem;
    align-items: center;
    margin-bottom: 1rem;
}

div.passport br {
    display: none;
}

img.comic {
    background-image: radial-gradient(rgb(20, 20, 20) 0%, rgb(20, 20, 20) 20%, transparent 20%), radial-gradient(rgb(20, 20, 20) 0%, rgb(20, 20, 20) 20%, transparent 20%);
    background-repeat: repeat;
    background-size: 0.5rem 0.5rem;
    background-position: 0 0, 0.25rem 0.25rem;
    background-color: rgb(254, 254, 254);
    border-radius: 1rem;
    box-shadow: 0 0 0.25rem rgba(0, 0, 0, 0.2);
}

div.newbee {
    align-self: center;
    mask-image: linear-gradient(to bottom, #000 0%, transparent 100%);
    -webkit-mask-image: linear-gradient(to bottom, #000 0%, transparent 100%);
}

div.nbtitle {
    text-align: center;
    align-self: center;
    font-size: 150%;
}

div.nbtitle h1,
div#page-content div.nbtitle p {
    margin: 0;
}

div.nbtitle a,
div.nbtitle a:hover,
div.nbtitle a:focus {
    color: inherit;
    text-decoration: none;
}

img.newbee {
    margin: 0.25rem 0.25rem 0 0.25rem;
    border-radius: 1rem 1rem 0 0;
    box-shadow: 0 0 0.25rem rgba(0, 0, 0, 0.2);
}

div.imgnewbee {
    display: grid;
    margin: 0.25rem 0.25rem 0 0.25rem;
    grid-template-areas: "a b c d";
    grid-template-columns: 1fr 1fr 1fr 1fr;
    border-radius: 1rem 1rem 0 0;
    box-shadow: 0 0 0.25rem rgba(0, 0, 0, 0.2);
    overflow: hidden;
}

div.hor {
    height: 15rem;
}

div.hors {
    height: 5rem;
}

div.out-c {
    max-width: 0% !important;
    margin-inline: auto;
}

div.in-c {
    max-width: 100vw !important;
    width: 100vw;
    margin-left: -50vw;
}

div.math-equation {
    max-width: 100%;
    overflow-x: auto;
}

span.equation-number {
    display: none;
}

div.code * {
    margin-block: 0;
}

div#page-content div.list-pages-item {
    position: relative;
    transition: ease 0.125s;
}

div#page-content div.list-pages-item:hover {
    background-color: rgb(var(--basalt-secondary-color));
}

div#page-content div.list-pages-item ul {
    margin-block: 0;
}

div#page-content div.list-pages-item ul.entry + br {
    display: none;
}

div#page-content div.list-pages-item div.preview {
    display: block;
    position: absolute;
    padding: 0.5rem 1rem;
    box-shadow: 0 0 0.25rem rgba(0, 0, 0, 0.2);
    border-radius: 1rem;
    background-color: rgb(var(--basalt-secondary-color));
    z-index: 5;
    opacity: 0;
    pointer-events: none;
    transition: ease 0.125s;
    margin-left: 2.5rem;
}

div#page-content div.list-pages-item > p,
div#page-content div.list-pages-item div.preview p {
    margin-block: 0;
}

div#page-content div.list-pages-item:hover div.preview {
    opacity: 1;
}

div.prev {
    display: none;
}

div.prev:has(.preview) {
    display: block;
}

div.rd div.blockquote {
    margin: 0;
}

div.rd {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: 1fr 1fr;
    gap: 1rem;
}

div.rd div.preview {
    display: inline;
}

div.collapsible-block-folded,
div.collapsible-block-unfolded-link {
    text-align: center;
}

@media (max-width: 1024px) {
div#content-wrap::before {
    max-height: 40vh;
}

div#content-wrap::after {
    top: min(5rem, 8vh);
    max-height: 40vh;
}

div.passport {
    display: block;
    text-align: center;
}

div.passport li,
div.passport div {
    text-align: left;
}

div#page-content div.passport div.blockquote.pass {
    margin-block: 1rem;
}

div.passport img.comic {
    max-width: max(50%, 300px);
}

div.out-c {
    max-width: 100vw !important;
}

div.in-c {
    max-width: 100vw !important;
    width: auto;
    margin-left: 0;
}

div.rd {
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr;
}
}
