[[module listpages created_at="@URL|2013.7" limit="1"]]
[[%%content{0}%%html]]
<input type="number" id="y" min="2014" step="1">年<input type="number" id="m" min="1" max="12" step="1">月
<input type="button" onclick="red();" value="表示">
<script>
var id = (val) => document.getElementById(val);

var url = "http://ja.scp-wiki.net/news-jp-admin";

var rawdate = "%%created_at%%";
var unix = Number(rawdate.replace(/\%\%date\|([0-9]*?)\%\%/g, "$1"))*1000;
var date = new Date(unix);
var y = date.getFullYear();
var m = date.getMonth()+1;
id("y").value = y;
id("m").value = m;

if(y == 2013 && m == 7) {
    var new_date = new Date();
    y = new_date.getFullYear();
    m = new_date.getMonth()+1;
    id("y").value = y;
    id("m").value = m;
    red();
}

function red() {
    y = id("y").value;
    m = id("m").value;
    parent.location.replace(url + "/created_at/" + y + "." + m);
}
</script>
[[/html]]
[[/module]]

