[[include :scp-jp-sandbox2:inc:jstyles]]
[[module css]]
.page-rate-widget-box, .creditButton{
    filter: grayscale(100%);
}
.rate-box-with-credit-button .creditButton{
    background-color: #3e3e3e;
    border: 1px solid #3e3e3e;
}

.modalbox .modalbox-title{
    background: #3e3e3e;
}
.modalbox .modalbox-title h2{
    color: #f7f7f7;
}

 .news{
    display: flex;
    /* Safari */
    flex-direction: row-reverse;
}
 .news-content{
    flex: 1;
    width: 0;
    padding-right:1em;
}
 .title{
    width: 100px;
    text-align:center;
    font-family: "ヒラギノ明朝 ProN W6", "HiraMinProN-W6", "HG明朝E", "ＭＳ Ｐ明朝", "MS PMincho", "MS 明朝", serif;
}
 .off_title{
    text-align:center;
    font-family: "ヒラギノ明朝 ProN W6", "HiraMinProN-W6", "HG明朝E", "ＭＳ Ｐ明朝", "MS PMincho", "MS 明朝", serif;
    display:none;
}
 #page-content .scoop h1{
    background-color: lightblue;
    text-shadow: rgb(238, 0, 0) 2px 0px 0px, rgb(238, 0, 0) 1px 0px 0px, rgb(238, 0, 0) 1px 1px 0px, rgb(238, 0, 0) 0px 2px 0px, rgb(238, 0, 0) 0px 1px 0px, rgb(238, 0, 0) -1px 1px 0px, rgb(238, 0, 0) -1px 0px 0px, rgb(238, 0, 0) -1px 0px 0px, rgb(238, 0, 0) -1px -1px 0px, rgb(238, 0, 0) 0px -1px 0px, rgb(238, 0, 0) 0px -1px 0px, rgb(238, 0, 0) 1px -1px 0px, rgb(238, 0, 0) 1px 0px 0px;
}
#page-content h1{
    color: white;
    background-color: #333;
    padding: 0.2em 0.5em;
    font-weight: normal;
    font-size: 2em;
    margin-bottom: 1em;
}
 #page-title {
    color: #000;
    font-family: "ヒラギノ明朝 ProN W6", "HiraMinProN-W6", "HG明朝E", "ＭＳ Ｐ明朝", "MS PMincho", "MS 明朝", serif;
    font-size: 0.4em;
    text-align:center;
}
 #container-wrap{
    background-image: url(http://scp-jp.wdfiles.com/local--files/deathcontest/Unknown-2.png) !important;
}
 #header{
    background:url(http://scp-jp.wdfiles.com/local--files/deathcontest/Unknown-3.png) 10px 40px no-repeat;
    background-size:100px 100px;
}
 .foldedtoc{
    border: double #000;
    width: fit-content;
}
 .toc-over{
    margin: 5px;
    padding: 5px;
}
 .foldedtoc .collapsible-block-link {
    text-decoration:none;
}
 #toc{
    margin: unset;
    padding: unset;
    border: unset;
    background-color: unset;
}
 #toc-action-bar{
    display: none;
}
 #toc-list {
    margin: 0.5em 0 0;
}
 .scoop-container{
    display: flex;
    /* Safari */
    flex-direction: row-reverse;
}
 .scoop-content{
    flex: 1;
}
 .manga{
    max-width:200px;
    margin-right:1.5em;
}
 @media (min-width: 767px) and (max-width: 900px){
    .scoop-container{
        flex-direction: column;
    }
    .manga{
        text-align:center;
        margin-right:0em;
    }
}
 @media (max-width: 630px) {
    .scoop-container{
        flex-direction: column;
    }
    .manga{
        text-align:center;
        margin-right:0em;
    }
}
 @media (max-width: 479px) {
    #header{
        background-position: 0 5.5em;
        background-size: 55px 55px;
    }
    .title{
        display:none;
    }
    .off_title{
        display:block;
    }
    .news-content{
        margin-right:0;
    }
}
 @media (min-width: 480px) and (max-width: 580px) {
    #header{
        background-position: 0.5em 4.5em;
        background-size: 66px 66px;
    }
}
 @media (min-width: 581px) and (max-width: 767px) {
    #header{
        background-size: 77px 77px;
    }
}
 #side-bar .heading {
    color: black;
}
 #side-bar{
    filter: grayscale(100%);
}
 #search-top-box {
    filter: grayscale(100%);
}
 .top-bar{
    filter: grayscale(100%);
}
 #header h2 span {
    color: #fbfbfb;
}
 h1, #page-title {
    color: black;
}
 .dc_divider5_black{
    background: url(data:image/svg+xml;base64,) no-repeat scroll center transparent;
    /* https://openclipart.org/detail/291267 */
    height: 35px;
    margin: 0px auto;
    padding: 6px;
    width: 95%;
    background-size: contain;
}

 .container {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
}
 .shell{
    min-width:300px;
    border-radius: 10px;
    border: 1px solid #497285;
    padding:8px;
    margin-bottom:8px;
}
 .shell h1{
    margin:0;
    padding: 0;
}
 .ribbon {
    display: inline-block;
    width: 173px;
    margin: 1em 0.5em;
    position: relative;
    transform: skewy(-3deg);
    backface-visibility: hidden;
    transition: 200ms all;
}
 .ribbon strong{
    text-transform: uppercase;
    font-family: 'Squada One', cursive;
    font-weight: 800;
    display: block;
    background: #FFF;
    padding: 2px 10px;
    color: #333;
    font-size: 35px;
    text-align: center;
    text-decoration: none;
    position: relative;
    z-index: 1;
    text-shadow: 1px 1px 0px #FFF, 2px 2px 0px #999, 3px 3px 0px #FFF;
    background-image: linear-gradient(to top, transparent 0%, rgba(0,0,0,.05) 100%);
    transition: 1s all;
    background-image: linear-gradient(to left top, transparent 0%, transparent 25%, rgba(0,0,0,.15) 25%, rgba(0,0,0,.15) 50%, transparent 50%, transparent 75%, rgba(0,0,0,.15) 75%);
    background-size: 4px 4px;
    box-shadow: 0 0 0 1px rgba(0,0,0,.4) inset, 0 0 20px -5px rgba(0,0,0,.4), 0 0 0px 3px #FFF inset;
}
 .ribbon:after, .ribbon:before {
    content: '';
    position: absolute;
    width: 50px;
    height: 100%;
    background: #BBB;
    transform: skewY(8deg);
    z-index: -3;
    border-radius: 4px;
}
 .ribbon:after {
    background-image: linear-gradient(to left, transparent, rgba(0,0,0,.4));
    right: 0;
    top: -4px;
}
 .ribbon:before {
    left: 0;
    bottom: -4px;
    background-image: linear-gradient(to right, transparent, rgba(0,0,0,.4));
}
 .feature-block .panel-body {
    height: 160px;
    overflow-y: hidden;
}
 .feature-block .feature-title {
    font-size: 120%;
    font-weight: bold;
}
 .feature-block .feature-subtitle {
    font-size: 90%;
}
 .feature-block .feature-title > p, .feature-block .feature-subtitle > p {
    margin: 0;
}


.lastyear, .lastyear h4{
    font-family: ヒラギノ明朝 ProN W6, HiraMinProN-W6, HG明朝E, ＭＳ Ｐ明朝, MS PMincho, MS 明朝;
}

.Dame{
/*赤い下線*/
text-decoration:underline;
text-decoration-color:red;
}
