[[module css]]
#login-status, #login-status #my-account, #login-status #account-topbutton, .rateup a, .ratedown a {
    color: #555 !important;
}

.page-rate-widget-box .rate-points, .cancel.btn.btn-default {
    background-color: #555 !important;
}

.cancel a {
    color: #999999 !important;
}

.cancel a:hover {
    background-color: #555 !important;
}

.rateup, .ratedown, .rate-points, .cancel {
    border-color: #555 !important;
}
.yui-navset.yui-navset-top {
    border:none;
}
.yui-navset .yui-nav {
    position: relative;
    z-index: 0;
    border: none;
    padding-left: 0.2em;
}
.yui-navset .yui-nav li {
    padding: 0;
    margin: 0.2em 0.3em;
    transition: transform 0.2s ease-in-out;
}
.yui-navset .yui-nav li a {
    background: transparent;
    color: #5f6368;
    border: 1px solid rgba(0,0,0,0.12);
    transition: background 0.1s ease-in-out,
                color 0.1s ease-in-out,
                border 0.1s ease-in-out;
}
.yui-navset .yui-nav li a:hover {
    background: #f8f9fa;
    color: #202124;
    border: 1px solid #a2a9b1;
}
.yui-navset .yui-nav li a em {
    border: none;
    padding: 0.5em 0.5em calc(0.5em - 2px) 0.5em;
}
.yui-navset .yui-nav .selected {
    padding: 0;
    margin: 0.2em 0.3em;
    transform: scale(1.1);
}
.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
    padding: 0;
    border: 1px solid #a2a9b1;
    background: #ebecee;
    color: #202124;
    font-weight:bold;
}
.yui-navset .yui-nav .selected a em {
    padding: 0.5em 0.5em calc(0.5em - 2px) 0.5em;
    border: none;
}

.yui-navset .yui-content {
    display: flex;
    flex-direction: column;
    background-color: transparent;
    border: none;
    padding: 0;
    position: relative;
    margin-top: 0.2em;
    transform-origin: 0 0;
}
.yui-navset .yui-content > div {
    border: none;
    background-color: transparent;
    padding: 0.25em 0.5em;
    display: block;
    top: 0;
    overflow: hidden;
    transform-origin: 0 0;
}
#page-content .yui-navset .yui-content > div > * {
    transform-origin: 0 0;
}
#page-content .yui-navset .yui-content > div[style*="none"] {
    display: block !important;
    flex: 0;
    max-height: 0;
    padding: 0 0.5em;
    border-width: 0;
    /* The following transition affects the one that DISAPPEARS */
    transition: padding 0s linear 0.5s,
                border-width 0s linear 0.5s,
                flex 0.5s cubic-bezier(.0,1.27,.0,.89) 0s;
    animation: tab-disappear 0.5s ease-in-out 0s 1 both;
}
#page-content .yui-navset .yui-content > div[style*="block"] {
    display: block !important;
    flex: 1;
    max-height: 9999rem;
    /* The following transition affects the one that APPEARS */
    transition: padding 0s linear 0.5s,
                border-width 0s linear 0.5s,
                flex 0.5s cubic-bezier(.99,.0,.99,.0) 0.5s;
    animation: tab-appear 0.5s ease-in-out 0.5s 1 both;
}

@keyframes tab-disappear {
    0% { max-height: 9999rem; }
    1% { max-height: 100vh; }
    100% { max-height: 0; }
}
@keyframes tab-appear {
    0% { max-height: 0; }
    99% { max-height: 100vh; }
    100% { max-height: 9999rem; }
}

div#container-wrap {
    background-image: none;
}

body {
    background-image: linear-gradient(to bottom,#e0e0e0, #e0e0e0 90px,#e0e0e0 90px, #ffffff 200px,#ffffff 200px, #ffffff 100%);
    background-repeat: no-repeat;
}

#top-bar div.open-menu a {
    border: 1px solid #333;
    border-radius: 0;
    box-shadow: none;
}

#header h1 {
    margin: auto;
    text-align: center;
    width: 100%;
}

#header {
    background-image: none;
}

#header h1 a::before {
    content: "Site-A9";
    font-weight: 400;
    font-size: 1.4em;
    color: black;
    text-shadow: none;
}

#top-bar {
   display: flex;
   justify-content: center;
   right: 0;
   top: 7.9rem;
}

#top-bar, #top-bar a {
    color: #333333;
}

#page-title {
    display: none;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    background: #333333;
    box-shadow: none;
    border-radius: 0px;
    color: #efefef;
}

#login-status, #login-status #my-account, #login-status #account-topbutton {
    color: #999999;
}

#search-top-box input.empty {
    color: #999999;
}

#search-top-box-input {
    height: 16.2px;
}

#search-top-box {
    top: 2.3rem!important;
    right: 8px;
}

#header::after {
    content: " ";
    background-image: url("https://scpsandboxcn.wdfiles.com/local--files/collab:a9/scp-A9-logo.png");
    background-repeat: no-repeat;
    background-position: center;
    background-size: 200px 200px;
    opacity: 0.5;
    width: 100%;
    height: 200px;
    position: absolute;
    top: -20px;
    left: 0;
    z-index: -1;
}

#header h1 span, #header h2 span {
    display: none;
}

div.gallery-box {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(min(calc(7.5rem), 100%), 1fr));
    gap: 1em;
    justify-content: center;
    width: max-content;
    margin: 1em auto;
    overflow: unset;
}

div.gallery-item[class] {
    border: none;
    margin: auto;
    padding: 0.25em;
    width: max-content;
    height: max-content;
}

div.gallery-item :where(table, tbody, tr, td) {
    display: contents;
}

div.gallery-box a {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 7.5rem;
    height: 7.5rem;
    background-color: rgb(255, 255, 255);
    border: 0.1rem rgb(225, 225, 225) solid;
    box-sizing: border-box;
    margin: auto;
    outline: 0 rgb(187, 0, 17) solid;
    transition: all 125ms linear;
}

div.gallery-box a:where(:hover, :focus) {
    outline-width: 0.2rem;
    transform: scale(1.5);
}
