[[include :scp-wiki:theme:black-highlighter-theme]]
[[include :scp-wiki:component:toggle-sidebar-bhl]]
[[include :scp-wiki:theme:present-malice]]
[[include :scp-wiki:component:centered-header-bhl |malice=--]]]

[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Cutive+Mono&display=swap');
@import url('https://scp-wiki.wdfiles.com/local--files/nightmare-begins/stylesheet.css');

/*-----------------------------------------------*/
/* Centered Header Fix */

#header::before {
   opacity: 0.35;
}

#header h1 a::before {
   -webkit-text-stroke: 0rem rgb(26, 0, 0);
   color: rgba(0, 0, 0, 0);
}

#header h1 a::after {
   filter: drop-shadow( 0rem 0.2rem 0.1rem rgba(0, 0, 0, 0.6) );
}

:root {
   --header-subtitle: "SECURE · CONTAIN · PROTECT";
}

#header h2 span::before {
   -webkit-text-stroke: 0rem rgba(0, 0, 0, 0);
   --wght: 200;
}

#header h2 span::after {
   content: none;
}
/*-----------------------------------------------*/

#page-title {
  display: none;
}

#page-content {
   font-size: 1.1em;
}

/*-----------------------------------------------*/
/* Teletype and ~ Element */

.teletype {
   filter: blur(0.5px);
   background: #f5f3e9;
   padding-left: 1em;
   padding-right: 1em;
   margin: 0em;
   font-family: 'Artisan12', monospace;
}

.tel-image {
   background: #f5f3e9;
   padding: 0em;
   margin: 0em;
}

.tel-overlay {
   position: relative;
}

.tel-overlay::before {
   content: "";
   position: absolute;
   background-image: url("https://scp-wiki.wdfiles.com/local--files/nightmare-begins/overlay.png");
   mix-blend-mode: hard-light;
   opacity: 0.90;
   height: 100%;
   width: 100%;
   z-index: 1;
}

.blurbox {

}

h3 {
   text-align: center;
   margin-bottom: 0em;
}

/*-----------------------------------------------*/
/* Styling for Navigation */
.minihub {
   font-size: 0.9em;
}

.minihub > .image-container {
   padding: 0px;
}

/*-----------------------------------------------*/
/* Widebox for Large Elements */
.widebox {
   width: 60vw;
   max-width: 60vw !important;
   position: relative;
   left: calc(-30vw + 50%);
}

@media screen and (max-width: 640px) {
  .widebox{
    width: 100vw;
    max-width: 100vw !important;
    left: calc(-50vw + 50%);
  }
}
/*-----------------------------------------------*/
