#page-title {display: none ;}

body {
    background: rgb(250, 252, 255);
    color: rgb(67, 67, 67);
    font-size:15px;
}
#search-top-box-input {
    border: solid 1px #ffffff;
    border-radius: 1px;
    color: rgb(250, 252, 255);
    background-color: rgb(250, 252, 255);
    box-shadow: inset 0px 0px 1px rgba(0,0,0,.5);
}
#search-top-box-input:hover,
#search-top-box-input:focus {
    border: solid 1px #fff;
    color: rgb(7, 55, 99);
    background-color: rgb(7, 55, 99);
    box-shadow: inset 0px 0px 1px rgba(0,0,0,.8);
}
#search-top-box-form input[type=submit] {
    border: solid 1px #ffffff;
    border-radius: 5px;
    padding: 2px 5px;
    font-size: 90%;
    font-weight: bold;
    color: rgb(7, 55, 99);
    background-color: rgb(28, 69, 135);
    background: linear-gradient(to bottom, #ffffff,#ffffff,#ffffff);
    box-shadow: 0 1px 3px rgba(0,0,0,.5);
    cursor: pointer;
}
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: solid 1px #fff;
    color: rgb(28, 69, 135);;
    text-shadow: 0 0 1px rgba(255,255,255,.25)
    background-color: rgb(28, 69, 135);
    background: linear-gradient(to bottom, #f5f5f5,#f5f5f5,#f5f5f5);
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

a,a:visited {
    color: rgb(7, 55, 99);
}
div#side-bar div.side-block {
    background: rgb(255, 255, 255);
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(28, 69, 135);
    border-color: rgb(28, 69, 135);
}
div.open-menu a {
    color: rgb(28, 69, 135)!important;
    border-color: rgb(28, 69, 135)!important;
}

div#container-wrap {
    background: url(http://ja.scp-wiki.net/local--files/nisso-medical-equipment-co-jp-rd-portal-contents-story-002/%E3%83%8B%E3%83%83%E3%82%BD%E5%8C%BB%E6%A9%9F%E3%83%98%E3%83%83%E3%83%80%E3%83%BC.png) top left repeat-x;
}
div#header {
    background: url(http://ja.scp-wiki.net/local--files/nisso-medical-equipment-co-jp-rd-portal-contents-story-002/%E3%83%8B%E3%83%83%E3%82%BD%E5%8C%BB%E6%A9%9F%E3%83%AD%E3%82%B4.png) 10px 40px no-repeat;
    background-position: 0em -1em; background-size 88px 88px;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: " ";
    color: #eee;
    text-shadow: 1px1px1px rgba(0,0,0,.8);
}
div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: " ";
    font-weight: bold;
    color: #f0f0c0;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0,3 .8);
    white-space: pre;
}
div#side-bar div.side-block {
    background: rgb(255, 255, 255);
}
div#side-bar div.side-block div.heading,
div#side-bar div.collapsible-block-unfolded-link,
div#side-bar div.collapsible-block-unfolded-link div.collapsible-block-link {
    color: rgb(28, 69, 135);
    border-color: rgb(28, 69, 135);
}
div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: rgb(28, 69, 135)!important;
    border: solid rgb(28, 69, 135) 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid rgb(28, 69, 135)!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: rgb(255, 255, 255);
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: rgb(28, 69, 135)!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: rgb(255, 255, 255)!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: transparent!important;
    color: transparent!important;
}
.rate-box-with-credit-button .creditButton {
    background-color: rgb(28, 69, 135)!important;
    border: solid 1px rgb(28,69,135)!important;
}
.creditButton a:hover {
color: transparent!important;
}
.color-green{
    color:#000000;
}
.color-blue{
    color:#696969;
}
.color-yellow{
    color:#b22222;
}

.c-SpliteImg {
  display: flex;
  flex-flow: row wrap;
}
.c-SpliteImg__cell {
  width: 100%;
}
.c-SpliteImg__cell > * {
  display: inline-block;
}
.c-SpliteImg__cell--x2 > * {
  width: 50%;
}
.c-SpliteImg__cell--x3 > * {
  width: 33.3333333333%;
}
.c-SpliteImg__cell--x4 > * {
  width: 25%;
}
.c-SpliteImg__cell--x5 > * {
  width: 20%;
}
.c-SpliteImg__cell--x6 > * {
  width: 16.6666666667%;
}
.c-SpliteImg__cell--x7 > * {
  width: 14.2857142857%;
}
.c-SpliteImg__cell--x8 > * {
  width: 12.5%;
}
.c-SpliteImg__cell--x9 > * {
  width: 11.1111111111%;
}
.c-SpliteImg__cell--x10 > * {
  width: 10%;
}
.c-SpliteImg__cell--x11 > * {
  width: 9.0909090909%;
}
.c-SpliteImg__cell--x12 > * {
  width: 8.3333333333%;
}

@media (max-width: 479px) {
#search-top-box-input {
    display: none;
}
[[/module]]

