[[include info:end-standalone]]

[[module css]]
@import url(//fonts.googleapis.com/earlyaccess/notosanskr.css);

.ndmbox {
    --ndm-banner-lines: rgba(115 70 70 / 75%);
    --ndm-banner-bg: #603a3a;
    margin-block: 2rem;
}
.ndmbox__banner {
    display: inline-block;
    width: calc(100% - 0.5rem);
    padding: 0.25rem 0 0.25rem 1.25rem;
    background-size: 3rem 4.35rem;
    background-image: linear-gradient(125deg,
        var(--ndm-banner-lines) 25%,
        transparent 25%,
        transparent 50%,
        var(--ndm-banner-lines) 50%,
        var(--ndm-banner-lines) 75%,
        transparent 75%,
        transparent 100%);
    background-color: var(--ndm-banner-bg);
    color: white;
    font-size: 1.5rem;
    font-weight: bold;
    text-transform: uppercase;
}
.ndmbox__banner h2 {
    margin: 0;
    font-size: 1.5rem;
    font-family: 'Noto Sans KR', sans-serif;
    font-weight: bold;
    letter-spacing: 0;
}
.ndmbox__content {
    padding: 0.125em 1rem;
    background-color: #f4f4f4;
    border-left: 5px solid var(--ndm-banner-bg);
}

.ndmbox--blanc {
    --ndm-banner-lines: rgba(246 246 246 / 75%);
    --ndm-banner-bg: #e6e6e6;
}
.ndmbox--blanc .ndmbox__banner {
    color: black;
}
.ndmbox--bleu {
    --ndm-banner-lines: rgba(0 77 230 / 75%);
    --ndm-banner-bg: #003cb4;
}
.ndmbox--vert {
    --ndm-banner-lines: rgba(1 125 4 / 75%);
    --ndm-banner-bg: #076403;
}
.ndmbox--jaune {
    --ndm-banner-lines: rgba(255 250 7 / 75%);
    --ndm-banner-bg: #fbe500;
}
.ndmbox--jaune .ndmbox__banner {
    color: black;
}
.ndmbox--orange {
    --ndm-banner-lines: rgba(255 84 0 / 75%);
    --ndm-banner-bg: #d74200;
}
.ndmbox--rouge {
    --ndm-banner-lines: rgba(230 0 0 / 75%);
    --ndm-banner-bg: #b40000;
}
.ndmbox--noir {
    --ndm-banner-lines: rgba(40 40 40 / 75%);
    --ndm-banner-bg: #161616;
}
[[/module]]

