[[module css]]
@import url('https://fonts.googleapis.com/css2?family=Courgette&display=swap');

.fancyhr hr {
    background-color: transparent !important;
}

.bubble {
    align: center;
    margin-left: 3px;
    margin-right: 3px;
    width: auto;
    padding: 10px;
    border: 3px solid var(--accentColor);
    border-radius: 12px;
    background-color: #21252E;
}

.brief {
    align: center;
    width: 66%;
    margin: auto;
    padding: 12px;
    border-radius: 12px;
    background-color: var(--accentColor);
    color: #21252E !important;
}

body {
    background-image: linear-gradient(
        to bottom,
        #FFD700, #2F333C 200px,
        #2F333C 200px, #2F333C 100%);
}

:root {
  --header-title: "OVERWATCH";
  --header-subtitle: "SACRIFICE CONFERS POWER";
  --lgurl: url('https://scp-wiki.wdfiles.com/local--files/fragment%3Ascp-6500-2/overwatchheader.svg');
  --accentColor: #FFD700;
}

div#extra-div-1 {
    filter: brightness(100%);
    height: 100px;
    margin-top: 30px;
}

#header h1 a::before {
    color: transparent;
}

#header h2::before {
    color: transparent;
}

.fancyborder {
    border-radius: 5rem;
}

@media (max-width: 706px) {
    .fancyborder {
        border-radius: 1rem;
    }
}

#login-status {
    color: #21252E;
}
#login-status a {
    color: #21252E;
}
#login-status ul a {
    color: #ededed;
    background: #21252E;
}
#login-status ul a:hover {
    color: #21252E;
}
#account-topbutton{
    border: solid 1px #21252E;
}

.cards {
    display: flex;
    flex-wrap: nowrap;
    justify-content: space-around;
    align-items: flex-start;
    flex-direction: row;
    align-content: center;
}

.cards > div {
    margin: -12px;
}

@import url("https://fonts.googleapis.com/css2?family=EB+Garamond:ital,wght@0,400..800;1,400..800&display=swap");

.cursive {
    font-family: "EB Garamond", Big Caslon, Book Antiqua, Palatino Linotype, Georgia, serif;
}

@media (min-width: 768px) {
    #main-content {
       max-width: 98%;
    }
    .mb {
        display: none;
    }
}

#container-wrap-wrap {
    overflow-x: hidden;
}

@media (max-width: 767px) {
    .custom {
        font-size: 60%;
    }
    .dt {
        display: none;
    }
    .cards {
        margin: 0px !important;
    }
    #main-content {
        margin: 0 1% 0 1%;
        max-width: 100%;
    }
}

.graybox {
    --accentColor: #ccc;
}

.greenbox {
    --accentColor: #4b2;
}

.bbox {
    --accentColor: #21252E;
}

.custom2 {
    width: auto !important;
    margin: 0px;
    padding: 0px;
}
[[/module]]

