[[module CSS]]

/* 页面版式更改 */

#main-content {
    max-width: 100vw !important;
}

#page-content {
    width: 100vw;
    margin: 0 auto;
    line-height: 2rem;
}

@media (max-width: 876px) {
    #page-content {
        padding: 0rem;
    }
}

span.shr-image {
    font-size: 0;
    background: url(https://scpsandboxcn.wikidot.com/local--files/oxygen9-files/o9logo.png) no-repeat center;
    background-size: contain;
    width: 4.5rem;
    height: 2.5rem;
}

.shr p::before, .shr p::after {
    display: block;
    width: -webkit-fill-available;
    height: 0.1rem;
    content: '';
    background: var(--mox-alter-color);
}

#main-content a[href="#u-bottom"]:not(.btn):visited, #main-content a[href="#u-top"]:not(.btn):visited, #main-content a[href="#u-bottom"]:not(.btn), #main-content a[href="#u-top"]:not(.btn) {
    color: #ccc;
    font-weight: bold;
}

#main-content a[href="#u-bottom"]:not(.btn):visited:hover, #main-content a[href="#u-top"]:not(.btn):visited:hover, #main-content a[href="#u-bottom"]:not(.btn):hover, #main-content a[href="#u-top"]:not(.btn):hover {
    box-shadow: none;
    text-shadow: 0 0 4px white;
}

.page-source::before {
    content: '有代码问题其实可以直接问我的，这个作者页的css写的基本上是一大坨(#｀-_ゝ-) ';
    display: block;
    color: #4b4b4b;
}

div#page-options-bottom, div#page-options-bottom-2 {
    width: 85%;
    margin: 1.5rem auto;
}

.footnotes-footer {
    width: 85%;
}

[[/module]]

[[module CSS]]

/* 竞赛展示CSS */

.contest-container {
    line-height: 1.25rem;
}

#main-content div.contest-single:nth-child(1) {
    box-shadow: inset -0.5rem 0rem 0rem #ffd700;
}

#main-content div.contest-single:nth-child(2) {
    box-shadow: inset -0.4rem 0rem #a2adbd;
}

#main-content div.contest-single:nth-child(3) {
    box-shadow: inset -0.3rem 0rem #886c3c;
}

.contest-single:nth-child(even) {
    background: #353535;
}

.contest-single:nth-child(odd) {
    background: #2d2d2d;
}

.contest-single {
    display: grid;
    padding: 0rem 0rem;
    grid-template-columns: 15% 25% 35% 25%;
    align-content: stretch;
    justify-items: start;
}

div.contest-single:nth-child(1) div.contest-index {
    background: linear-gradient(295deg, #906f2f, #ffd700);
    color: #332e24;
}

div.contest-single:nth-child(2) div.contest-index {
    background: linear-gradient(295deg, #beb7e9, #9fafb9);
    color: #272433;
}

div.contest-single:nth-child(3) div.contest-index {
    background: linear-gradient(295deg, #79573c, #b19573);
    color: #26231f;
}

div.contest-single>div.contest-index {
    display: block;
    /* flex-grow: 0.1; */
    /* width: 10%; */
    padding: 0.5rem 1rem;
    width: -webkit-fill-available;
}

span.contest-index.index::before {
    content: '#';
    font-size: 85%;
}

div.contest-single>div.contest-name {
    display: block;
    flex-grow: 0.3;
    width:  -webkit-fill-available;
    display: flex;
    flex-direction: column;
    padding: 0.5rem 1rem;
}

div.contest-single>div.contest-info1 {
    display: grid;
    flex-grow: 0.2;
    width:  -webkit-fill-available;
    font-size: 0.75rem;
    padding: 0.6rem 0.5rem;
    flex-wrap: wrap;
    column-gap: 0.5rem;
    justify-content: start;
    align-items: center;
    justify-items: start;
    grid-template-columns: 1fr 1fr;
}

div.contest-single>div.contest-info2 {
    display: block;
    flex-grow: 0.3;
    width:  -webkit-fill-available;
    text-align: right;
    padding: 0.3rem 1rem 0.3rem 0.3rem;
}

div.contest-index>p {
    display: flex;
    flex-direction: column;
}

span.contest-index.udscore.vote0::before {
    font-size: 0.6rem;
    content: '暂无投票';
}
span.contest-index.udscore.vote0 {
    font-size: 00;
}

span.contest-index.index {
    display: inline-block;
    font-weight: bolder;
    font-size: 2rem;
    width: -webkit-fill-available;
}

div.contest-index>p br {
    display: none;
}

span.contest-index.scoreinfo {
    word-break: keep-all;
    font-size: 80%;
}

span.contest-index.udscore::after {
    content: '%';
    font-size: 70%;
}

span.contest-name.scp {
    font-size: 120%;
    font-weight: bolder;
}

div.contest-name br {
    display: none;
}

span.contest-name.title {
    font-weight: bold;
    color: #bfbfbf;
}

span.contest-name.co-author br {
    display: inline;
}

div.contest-name.author p {
    margin: 0;
    padding: 0;
    display: inline;
    color: #979797;
}

div.contest-info1 p {
    display: contents;
}

div.contest-info1 p a {
    color: #c7c7c7;
    display: inline-block;
}

div.contest-info1 p br {
    display: none;
}

span.contest-info2.c-tags {
    font-size: 0.65rem;
    color: #787878;
}

#main-content div.contest-single:nth-child(1) div.contest-name a {
    color: #ffc000;
    text-shadow: 0 0 0.7rem #ffc0007d;
}

#main-content div.contest-single:nth-child(2) div.contest-name a {
    color: #b9beff;
    text-shadow: 0 0 0.7rem #656fbe7d;
}

#main-content div.contest-single:nth-child(3) div.contest-name a {
    color: #b49581;
    text-shadow: 0 0 0.7rem #9870597d;
}

@media(max-width: 867px) {

div.contest-single {
    display: grid;
    padding: 0rem 0rem;
    justify-items: start;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: auto auto;
    justify-content: space-between;
}

div.contest-single>div.contest-index {
    width: auto;
    padding: 0.5rem 1rem;
    min-width: 5rem;
}

div.contest-single>div.contest-name {
    width: calc(100% - 2.35rem);
    word-break: break-all;
    text-align: left;
}

    div.contest-single>div.contest-info1, div.contest-single>div.contest-info2 {
        width: -webkit-fill-available;
        display: block;
    }

}

span.contest-name.co-author::before {
    content: '，以及';
}

/* 2025 goif */

.goi-display > a[href^="/system:page-tags/tag/_2025goif"] { display: none; }

.goi-display > a {color: #EEEEEE}

[[/module]]

[[module CSS]]

:root {
    counter-reset: sumup 0 votes 0 words 0 revs 0 pages 0;
}

.data st p, .data st div {
    display: contents;
}

.score-sumup::after {
    content: counter(sumup);
}

.votes::after {
    content: counter(votes);
}

.words::after {
    content: counter(words);
}

.revs::after {
    content: counter(revs);
}

.pages::after {
    content: counter(pages);
}

[[/module]]

[[module CSS]]

/* 元素CSS */

.ir-modal {
    position: relative;
    width: 100%;
    height: 90vh;
    background-size: cover;
    background-position: center;
    display: flex;
    margin: 5rem auto;
}

.ir-imgcover {
    position: relative;
    width: 100%;
    height: 100%;
    background-image: linear-gradient(90deg, #101010 0%, #00000080 80%, #101010 100%);
    display: grid
;
    align-content: stretch;
    transition: all 0.35s;
    backdrop-filter: brightness(0.5);
    grid-template-areas:
        "subtitle ."
        "title item"
        "intro intro2"
        "link .";
    align-items: end;
    justify-items: stretch;
    grid-auto-rows: 1fr 0.6fr 0.9fr 1fr;
    backdrop-filter: brightness(1.3) grayscale(1);
}

.ir-imgcover:hover {
    backdrop-filter: brightness(0.5) grayscale(0);
}

.ir-imgcover>p, .ir-imgcover>p>br {
    display: contents;
}

span.ir-modal-p1 {
    line-height: 2.3rem;
    display: block;
    margin: 0rem 0rem 0rem 2rem;
    font-weight: 100;
    font-size: 2.3rem;
    letter-spacing: -0.2rem;
    color: #979797;
    grid-area: subtitle;
    transition-delay: 0s !important;
}

span.ir-modal-p2 {
    display: block;
    margin: 0rem 0rem 0rem 2rem;
    font-size: 5rem;
    font-weight: bolder;
    letter-spacing: -0.4rem;
    grid-area: title;
    line-height: 5rem;
    align-self: flex-start;
    transition-delay: 0.05s !important;
}

span.ir-modal-p3 {
    display: block;
    text-align: right;
    text-transform: uppercase;
    font-size: 2rem;
    float: right;
    margin: 0rem 3rem 0rem 0rem;
    grid-area: item;
    align-self: center;
    transition-delay: 0.1s !important;
}

span.ir-modal-p3 a {
    color: rgb(var(--basalt-main-text-color));
}

.ir-modal-p4 {
    float: left;
    width: calc(100% - 6rem);
    display: block;
    margin: 0.5rem 0rem 0rem 4rem;
    grid-area: intro;
    align-self: center;
    transition-delay: 0.2s !important;
}


.ir-modal-p4:before {
    content: '"';
    display: block;
    margin: 0rem 0rem -3.7rem -2rem;
    font-size: 3rem;
    font-weight: bolder;
    font-family: 'Jost';
}

span.ir-modal-p5 {
    grid-area: intro2;
    align-self: center;
    text-align: right;
    margin: 0rem 2rem 0rem 0rem;
    font-size: 1.25rem;
    transition-delay: 0.25s !important;
}

.ir-b-list {
    grid-area: link;
    margin: 0rem 0rem 2rem 2rem;
    transition-delay: 0.3s !important;
}

#main-content span.ir-modal-b a, #main-content span.ir-modal-b a:not(.btn):visited {
    display: inline-block;
    padding: 0.5rem 1.5rem;
    margin: 0rem 2rem 1rem 1rem;
    border: solid 3px #2b2b2b;
    font-size: 1.5rem;
    width: 8rem;
    text-align: center;
    color: rgb(136 136 136);
    transition: all 0.2s, transform 0.5s;
    background-color: #101010;
}

span.ir-disabled {
    display: inline-block;
    padding: 0.5rem 1.5rem;
    margin: 0rem 2rem 1rem 1rem;
    border: solid 3px #2b2b2b;
    font-size: 1.5rem;
    width: 8rem;
    text-align: center;
    color: rgb(55 55 55);
    transition: all 0.2s, transform 0.5s;
    background-color: #101010;
}

#main-content span.ir-modal-b.b2 a, #main-content span.ir-modal-b.b2 a:not(.btn):visited {
    font-size: 1rem;
    color: #6b6b6b;
}

#main-content span.ir-modal-b a:hover, #main-content span.ir-modal-b a:not(.btn):visited:hover {
    background-color: #C8C8C8;
    text-decoration: none;
    color: #242424;
    font-weight: 900;
    transform: scale(1.1);
    border: solid 3px #c8c8c8;
    box-shadow: none;
}

.ir-imgcover:has(span.ir-modal-b a:hover) {
    backdrop-filter: brightness(1.5);
}

/* 移动端 */

@media only screen and (max-width: 767px) {
    .ir-imgcover {
        padding: 0rem 0rem 0rem 0rem;
        grid-template-areas:
        "subtitle"
        "title"
        "item"
        "intro"
        "intro2"
        "link";
        grid-auto-columns: 1fr;
        grid-auto-rows: 1fr 1fr 1.5fr 3fr 2fr 1fr;
    }

    span.ir-modal-p1 {
        text-align: center;
        margin: 0rem auto;
    }

span.ir-modal-p2 {
        text-align: center;
        width: 13rem;
        margin: 1rem auto;
        line-height: 5rem;
    }

    span.ir-modal-p3 {
        float: none;
        text-align: center;
        margin: 0rem auto 0rem auto;
        font-size: 1.6rem;
        align-self: flex-start;
    }

    .ir-modal-p4 {
        float: none;
        width: 79%;
        margin: 0rem auto;
    }

    span.ir-modal-p5 {
        align-self: center;
        text-align: center;
        margin: 0rem 0rem 0rem 0rem;
    }

    .ir-imgcover .ir-b-list {
        text-align: center;
        margin: 0rem 0rem 0rem 0rem;
    }

    span.irsp1 {
        width: max-content;
        font-size: 3.4rem;
    }

    span.ir-modal-p2.sp-non4 {
        width: auto;
        text-align: left;
    }

    .ir-number {
        font-size: 13rem !important;
        margin: 0rem 0rem -30rem 0rem;
        letter-spacing: -2.6rem !important;
        line-height: 10rem !important;
    }

}

.ir-logo {
    background-image: url(https://www.wikudot.icu/i/0/s7e9wn.svg);
    width: 50vw;
    height: 50vh;
    margin: 2rem auto;
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;
    animation: logoReverse 8s infinite;
    position: relative;
}

@keyframes logoReverse {
0% {   opacity:1 ; }
10% {     opacity: 0 ; }
50% {     opacity: 0 ; }
60% {    opacity: 1; }
100% {    opacity:1 ; }
}

.ir-description {
    width: 60vw;
    margin: 0 auto;
    line-height: 1.7rem;
}

.disabled {
-webkit-user-select: none; /* Safari */
-moz-user-select: none; /* Firefox */
-ms-user-select: none; /* IE 10+ */
user-select: none; /* 标准语法 */
}



div.ir-modal>div.ir-imgcover :is(.ir-modal-p1, .ir-modal-p2, .ir-modal-p3, .ir-modal-p4, .ir-modal-p5, .ir-b-list) {
    opacity: 0;
    transition: opacity 0.75s, transform 1.25s;
}

div.ir-modal>div.ir-imgcover :is(.ir-modal-p1, .ir-modal-p2, .ir-modal-p4, .ir-b-list) {
    transform: translateX(-100vw);
}

div.ir-modal>div.ir-imgcover :is(.ir-modal-p3, .ir-modal-p5) {
    transform: translateX(100vw);
}

div.ir-modal>div.ir-imgcover:hover :is(.ir-modal-p1, .ir-modal-p2, .ir-modal-p3, .ir-modal-p4, .ir-modal-p5, .ir-b-list) {
    opacity: 1;
    transform: translateX(0);
}

.ir-show-frame.l .ir-modal {
    transform: perspective(500px) rotateX(-10deg) rotateY(20deg) rotateZ(10deg) translateX(-18vw) scale(0.5);
    transition: transform 0.25s;
}

.ir-show-frame.r .ir-modal {
    transform: perspective(500px) rotateX(10deg) rotateY(-20deg) rotateZ(-10deg) translateX(18vw) scale(0.5);
    transition: transform 0.25s;
}

.ir-show-frame.l .ir-modal:hover, .ir-show-frame.r .ir-modal:hover {
    transform: perspective(500px) rotateX(0deg) rotateY(0deg) rotateZ(0deg) translateX(0vw);
}

.r .ir-number {
    text-align: left;
}

.l .ir-number {
    text-align: right;
}

.ir-number {
    font-size: 19rem;
    z-index: 0;
    position: relative;
    margin: 0rem 0rem -30rem 0rem;
    width: 100%;
    font-family: 'Oxygen Mono';
    letter-spacing: -3rem;
    line-height: 16rem;
    font-weight: bolder;
    transform: translateY(0);
    opacity: 1;
    transition: transform 0.5s, opacity 0.5s;
}

span.ir-number-title {
    font-size: 2.5rem;
    font-family: 'Raleway';
    letter-spacing: -0.1rem;
    line-height: 2.6rem;
    font-weight: bolder;
}

.ir-show-frame:has(.ir-modal:hover) .ir-number {
    transform: translateY(10rem);
    opacity: 0;
}

.ir-short-display .modal {
    min-width: 24rem;
    width: 45%;
    transform: translateY(0);
    transition: transform 0.3s;
    break-inside: auto;
}

.ir-short-display .modal:hover {
    transform: translateY(-1rem) scale(1.05);
}

.ir-short-display {
    display: flex;
    width: 100%;
    column-gap: 0rem;
    flex-direction: row;
    flex-wrap: wrap;
    align-items: stretch;
}

#main-content .ir-short-display .modal div a:not(.btn):hover, .ir-short-display .modal a:not(.btn):visited:hover {
    box-shadow: none;
}

.modal.theme h2 span a:hover {
    box-shadow: 0 0.15rem #d3c313 !important;
}
.modal.theme h2 span a {
    color: #d3c313 !important;
}
.modal.theme {
    border-color: #d3c313;
}

.modal.story h2 span a:hover {
    box-shadow: 0 0.15rem #1cd559 !important;
}
.modal.story h2 span a {
    color: #1cd559 !important;
}
.modal.story {
    border-color: #1cd559;
}

.modal.art h2 span a:hover {
    box-shadow: 0 0.15rem #e94533 !important;
}
.modal.art h2 span a {
    color: #e94533 !important;
}
.modal.art {
    border-color: #e94533;
}


div.data-show span {
    font-family: 'Oxygen Mono';
    font-size: 1.5rem;
    font-weight: bolder;
}

div.data-show span::after {
    color: #ffb51c;
    letter-spacing: -0.1rem;
    font-size: 1.75rem;
}

div.data-st div.list-pages-box {
    display: block;
    line-height: 0;
    max-height: 0;
}


