[[module CSS]]
/*this css was mostly designed by aers and was edited by uncle nicolini*/
div#container-wrap {
    background: url(http://themes.wdfiles.com/local--files/helloworld/background.png) top left repeat-x;
    background-size: 105px;
}

#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 30px; /* FOR MENU */
    background: url(http://smlt.wdfiles.com/local--files/unclenicolini%3Atest/josie.png) 10px 35px no-repeat;
}

#header h1 a {
    font-family: "Times New Roman", Times, serif;
    content: "SCP Foundation";
}

#header h1 a::after {
    font-family: "Times New Roman", Times, serif;
    content: "SCP Foundation";
}

#header h1 a span{
    display: block;
    content: "";
    visibility: hidden;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::after {
    content: "To Store, Cache, and Preserve.";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #ffffff;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
[[/module]]
