[[module CSS]]
/* isolates wideness to this class */
.widebox {
   width: 99.3vw;
   max-width: 100vw !important;
   position: relative;
   left: calc(-49.65vw + 50%);
}

/* centers page title */
#page-title {
  text-align: center;
}

/* destroys the second brazil in the page */
#page-content p br:nth-child(2) {
  display: none;
}

.rmodecollection {
   display: flex;
   flex-flow: row wrap;
   justify-content: flex-start;
   padding: 1rem;
}

.rmodecollection .rmode {
  background: rgb(45, 45, 45);
  position: relative;
  box-sizing: border-box;
  width: calc(25% - (0.3em * 3));
  height: 3rem;
  margin: 0.3em;
  font-family: 'Work Sans', sans-serif;
  font-weight: 800;
  font-size: 90%;
  text-transform: uppercase;
}

.rmodecollection .rmode:hover {
  background: rgb(65,65,65);
}

.rmodecollection .rmode p {
margin: 0;
height: 100%;
}

.rm-link, .rm-link:hover, .rm-link:visited {
color: white;
display: block;
height: 100%;
width: 100%;
text-decoration: none;
}

.mainimage {
position: relative;
}

.mainimage #u-rm-unlit, .mainimage #u-rm-solid, .mainimage #u-rm-wireframe {
display: none;
position: absolute;
top: 1.3em;
}

.mainimage #u-rm-unlit:target {
display: block;
}

.mainimage #u-rm-solid:target {
display: block;
}

.mainimage #u-rm-wireframe:target {
display: block;
}

.related-flex {
    display: flex;
    justify-content: center;
}
.related {
    box-shadow: 0px 0px 8px 4px rgba(0, 0, 0, 0.7);
    background: #191919;
    padding: 0 1em;
    margin: 1em 0;
}
[[/module]]

