[[module css]]
body {
    background-image: linear-gradient(
        to bottom,
        #ddcfee, #2F333C 200px,
        #2F333C 200px, #2F333C 100%);
}

:root {
  --header-title: " ";
  --header-subtitle: " ";
  --lgurl: url('http://scp-jp-storage.wdfiles.com/local--files/file%3A2963469-55-celk/overwatchheaderthreshold.svg');
  --accentColor: #ddcfee;
}

div#extra-div-1 {
    filter: brightness(100%);
    height: 100px;
    margin-top: 30px;
}

#header h1 a::before {
    color: transparent;
}

#header h2::before {
    color: transparent;
}

.bt {
    filter: none;
}

@keyframes rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(359deg);
  }
}

.no {
    font-size: 125%;
}

.no:hover {
    filter: opacity(0.5);
    cursor: not-allowed;
}

#login-status {
    color: #21252E;
}
#login-status a {
    color: #21252E;
}
#login-status ul a {
    color: #ededed;
    background: #21252E;
}
#login-status ul a:hover {
    color: #21252E;
}
#account-topbutton{
    border: solid 1px #21252E;
}

.fancyborder {
    border-radius: 5rem;
}

.fancyborder .fnnum::after {
    color: #000;
}

@media (max-width: 706px) {
    .fancyborder {
        border-radius: 1rem;
    }
}

.cards {
    display: flex;
    flex-wrap: nowrap;
    justify-content: center;
}

.cards > .image-container {
    padding: 1px;
}

.yui-navset {
    margin: 1%;
}

.greenbox {
    --accentColor: #4b2;
}

.fancyhr {
    filter: grayscale(80%) hue-rotate(240deg);
}

[[/module]]

