[[module CSS]]
/* tech team member! expel your bowel! */

/* BHL dark mode except header */
:root {
  --swatch-background: var(--dark-gray-monochrome);

  --swatch-menubg-color: var(--dark-gray-monochrome);
  --sidebar-links-text: var(--white-monochrome);

  --swatch-text-dark: var(--white-monochrome);

  --rating-module-button-color: var(--white-monochrome);
    --rating-module-button-plus-color: 0, 200, 0;
    --rating-module-button-negative-color: 200, 0, 0;
    --rating-module-button-cancel-color: 200, 200, 200;
    --rating-module-button-credit-color: 50, 50, 50;
    --rating-module-text-color: var(--swatch-menutxt-light-color);
    --rating-module-text-hover-color: var(--swatch-menutxt-light-color);

  --background-gradient-distance: 0rem;
}

/* block quote */
blockquote,
div.blockquote {
  background-color: rgb(var(--dark-gray-monochrome));
  border-style: dashed;
    border-color: rgba(var(--white-monochrome),0.5);
    border-width: 0.05rem;
}

/* links ...... */
a,
a.newpage,
a:visited,
#side-bar a:visited {
    color: rgb(232, 60, 66);
}

/* hide fallback links... or atleast like   tries to??? fuck you safari */
.imagebox img {
  color: transparent;
}

/* tag style */
.page-tags span {
  border-top: 0;
}
.page-tags span a {
    display: inline-block;
    margin: 0 0 .5rem .75rem;
    padding: 0.1875rem 0.3125rem 0.1875rem 0;
    height: 0.8125rem;
    line-height: 13px;
    font-size: 11px;
    background: rgb(232, 60, 66);
    color: #fff;
    text-decoration: none;
    -moz-border-radius-bottomright: 4px;
    -webkit-border-bottom-right-radius: 4px;
    border-bottom-right-radius: 0.25rem;
    -moz-border-radius-topright: 4px;
    -webkit-border-top-right-radius: 4px;
    border-top-right-radius: 0.25rem;
}
.page-tags span a:before, .page-tags span a:after {
    content: "";
    float: left;
    position: relative;
}
.page-tags span a:before {
    padding:0 0.0625rem 0.1875rem;
    top: -0.1875rem;
    left: -0.625rem;
    width: 0;
    height: 0;
    border-color: transparent rgb(232, 60, 66) transparent transparent;
    border-style: solid;
    border-width: 0.5rem 0.5rem 0.5rem 0;
}
.page-tags span a:after {
    top: 0.28125rem;
    left: -0.5rem;
    width: 0.25rem;
    height: 0.25rem;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 0.125rem;
    background: rgb(252, 252, 252);
    -moz-box-shadow: -1px -1px 2px #004977;
    -webkit-box-shadow: -1px -1px 2px #333;
    box-shadow: -0.0625rem -0.0625rem 0.125rem rgb(48, 48, 52);
}
#main-content .page-tags span {
    max-width: 100%;
}

/* the header on top of edit page become white... */
#action-area h1 {
   color: var(--white-monochrome);
}

/* changes option hovertips to black... this doesn't work with variables for some reason? */
.hovertip {
  color: rgb(12, 12, 12);
}

/* text shadow fix for header */
#header h1 a::before, #header h2 span::before {
    --text-shadow: rgb(var(--black-monochrome, 12, 12, 12, ));
}

/* centers page title */
#page-title {
  border: none;
  padding: 0;
  margin: 0 auto;
}

/* no margin! */
.page-rate-widget-box {
  margin-bottom: 0;
}

/* makes the side bar cooperate with the image blocks */
#side-bar {
   z-index: 3;
}

/* isolates wideness to this class */
.widebox {
   width: 80vw;
   max-width: 80vw !important;
   position: relative;
   left: calc(-40vw + 50%);
}

/* contains multitudes..... and images */
.imagecollection {
   display: flex;
   flex-flow: row wrap;
   justify-content: flex-start;
   padding: 0.25vw;
}

/* makes ur image boxes square  */
.imagecollection .imagebox {
  background: transparent;
  position: relative;
  box-sizing: border-box;
  width: 26vw;
  height: 26vw;
  margin: 0.25vw;
}

/* makes the first image box wide */
.imagecollection .imagebox:first-child {
  width: 100%;
  height: 26vw;
  margin: 0.25vw;
}

/* adds that hover gradient to each image box */
.imagecollection .imagebox:before{
  content: "";
  position: absolute;
  background: linear-gradient(0deg, rgba(0,0,0,0.75) 0%, rgba(0,0,0,0) 35%);
  height: 100%;
  width: 100%;
  z-index: 1;
  opacity: 0;
  transition: 0.3s;
  pointer-events: none;
}

/* the image */
.imagecollection .imagebox img {
  display: block;
  margin: auto;
  height: 100%;
  width: 100%;
  object-fit: cover;
  object-position: center;
}

/* caption below every image ,,,. */
.caption {
  color: white;
  bottom: 0;
  padding: 1em;
  font-size: 2em;
  position: absolute;
  z-index: 2;
  opacity: 0;
  transition: 0.5s;
  pointer-events: none;
}

/* makes the gradient and caption appear on hover */
.imagecollection .imagebox:hover:before, .imagecollection .imagebox:hover .caption {
  opacity: 1;
}

/* mobile shit */
@media screen and (max-width: 1200px) {
  .imagecollection .imagebox {
    width: 39vw;
    height: 39vw;
    margin: 0.25vw;
  }
  .imagecollection .imagebox:first-child {
    height: 39vw;
  }
}

@media screen and (max-width: 640px) {
  .imagecollection .imagebox, .imagecollection .imagebox:first-child {
    width: 100vw;
    height: 56.25vw;
  }
  .widebox {
    width: 100vw;
    max-width: 100vw !important;
    left: calc(-50vw + 50%);
  }
}
/* Taggers cry */

.page-tags span a::after {
  width: 1rem;
  height: 1rem;
}
[[/module]]
