  font-size: 1.2em;
}

@media (max-width: 707px) {
    #header h1 a::before {
        font-size: 1.6em;
    }
}

#login-status,
#login-status a {
    color: #333333;
}

#page-title {
    display: none;
}

#footer, #footer a {
    background: transparent;
    color: #333333;
}

#search-top-box-input,
#search-top-box-input:hover,
#search-top-box-input:focus,
#search-top-box-form input[type=submit],
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    background: #333333;
    box-shadow: none;
    border-radius: 0px;
    color: #efefef;
}

#search-top-box input.empty {
    color: #999999;
}

#search-top-box {
    top: 2.3rem!important;
    right: 8px;
}

#top-bar { 
   display: flex;
   justify-content: center;
   right: 0;
   top: 7.9rem;
}

#top-bar, #top-bar a {
    color: #333333;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: 'Montserrat', sans-serif;
    color: #000;
    letter-spacing: 1px;
}

h1 {
    font-size: 2em;
}

h2 {
    font-size: 1.45em;
}

div#extra-div-1 {
    height: 160px;
    width: 100%;
    top: 0;
