    background-repeat: no-repeat;
    background-position: 50% 50%;
    z-index: -1;
}

@media (max-width: 707px) {
    div#extra-div-1 {
        top: 15px;
    }
}

body {
    background-image: linear-gradient(
        to bottom,
        #D2CCC5, #F9F6F3 90px,
        #F9F6F3 50px, #FFFEFB 200px,
        #FFFEFB 700px, #FFB95B 100%);
