}

:root {
  --timeScale: 1.5;
  --timeDelay: 1.5s;
  --posX: calc(50% - 358px - 13rem);
  --fnLinger: 1s;
}

#page-content hr {
    background-color: #000;
}

#page-content tr th {
    padding: 6px;
    border: #000 1px solid;
}

#page-content tr td {
    padding: 12px;
    border: #000 1px solid;
    line-height: 1.4;
}

#page-content .sidebox tr td, 
