[[module CSS]]
:root {

    --a: 0, 0, 0;
    --b: 188, 188, 188;
    --c: 228, 228, 228;

div[class$="_warn"] {
    background: var(--warn-logo) rgba(var(--warn-color), 1);
    background-size: contain;
    background-position: top center;
    background-repeat: no-repeat;
    min-height: 4rem;
    margin-inline: auto;
    margin-block: 2rem;
    padding-inline: 20px;
    padding-block: 10px;
    border: solid 2px rgba(0, 0, 0, 0.1);
    text-align: center;
}

div[class$="_warn"]::before {
    content: var(--warn-heading);
    font-family: var(--UI-font);
    font-weight: 900;
    font-size: 1.3em;
    text-align: center;
    margin: 0 auto 1rem auto;
    display: flex;
    flex-direction: column;
    padding-top: 0.5em;
}

div.raisa_warn {
    --warn-heading: "来自记录与信息安全管理部门的通知";
    --warn-logo: url(https://scp-wiki.wikidot.com/local--files/theme:basalt/RAISA_LIGHTLOGO.png);
    --warn-color: 242, 242, 192;
}

div.modal {
    display: block;
    margin-inline: auto;
    margin-block: 1.4rem;
    padding-inline: 1.5rem;
    padding-block: 0.5rem;
    background-color: rgb(var(--c));
    border: solid 0.2rem rgb(var(--b));
    color: rgb(var(--a));
}
[[/module]]

