[[module CSS]]
@import url(/deleted:component:resurrection-theme/code/1);
[[/module]]

[[code type="CSS"]]
/*
    Resurrection Theme
    Created for the SCP Foundation by thedeadlymoose.
    Edited for the SCP財団 by SCP-JP Technical Team
*/

@charset "utf-8";

@import url('http://scp-jp.wdfiles.com/local--theme/scp-sigma-9-off-canvas/style.css');
@import url('http://fonts.googleapis.com/css?family=Exo:400,700,600,500|Droid Sans:400,700|Exo+2:400,700,600,500');

/* COMMON */
h1, #page-title {
    font-family: "Exo", Arial, sans-serif;
}

h1 {
    font-family: "Exo", Arial, sans-serif;
    color: #FD6E20;
    font-weight: normal;
}

h2, h3, h4, h5, h6 {
    font-family: "Exo", Arial,sans-serif;
    color: #EEE;
}

h5{
    font-style: italic;
}

#page-title {
    border-bottom: 1px dashed #999;
}

li, p {
    line-height: 1.3rem;
}

a {
    color: #DDD;
}

hr {
    border-bottom: medium none;
    border-top: 1px dashed #999;
    background-color: transparent;
    color: transparent;
}

/* GLOBAL WIDTH */
body {
    background-color: #494949;
    font-size: 0.85rem;
    font-family: 'Droid Sans', Arial, sans-serif;
    color: #CCC;
}

div#container-wrap {
    background: url(http://scp-jp.wdfiles.com/local--files/deleted%3Acomponent%3Aresurrection-theme/bodybg.png) top left repeat-x;
}

/* HEADER */
#header {
    background: url(http://scp-jp.wdfiles.com/local--files/deleted%3Acomponent%3Aresurrection-theme/logo.png) 10px 40px no-repeat;
}

#search-top-box {
    padding: 0.5em;
}

#search-top-box input.empty {
    color: #DDD;
}

/* SEARCH BOX INPUT FIELD */
#search-top-box-input {
    background-color: #1A1A1A;
    width: 13em;
    height: 2.3em;
    margin-right: 0.5em;
    padding-right: 0.5em;
    border: none;
    border-radius: 3px;
    box-shadow: none;
    font-family: "Droid Sans", Arial, sans-serif;
    font-size: 95%;
    text-align: right;
}

/* SEARCH BOX INPUT FIELD OVER STATE */
#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #1A1A1A;
    border: none;
    box-shadow: none;
}

/* SEARCH BUTTON */
#search-top-box-form input[type=submit] {
    background-color: none;
    background: linear-gradient(to bottom, #FE9B26 50%,#FD6E20 50%);
    box-shadow: none;
    cursor: pointer;
    border: none;
    border-radius: 3px;
    height: 2.3em;
    font-family: "Exo", Arial, sans-serif;
    font-size: 100%;
    font-weight: normal;
    color: #000;
}

/* SEARCH BUTTON OVER STATE */
#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    border: none;
    color: #000;
    text-shadow: none;
    background-color: none;
    background: linear-gradient(to top, #FE9B26 50%,#FD6E20 50%);
    box-shadow: none;
}

/* SCP Foundation Title styles */ 
#header h1 {
    display: none; /*To remove SCP Foundation title*/
    /*margin-left: 120px;
    padding: 0;
    float: left;
    max-height: 95px;*/
}

#header h2 {
    display: none; /*To remove SCP Foundation title*/
    /*margin-left: 120px;
    padding: 0;
    clear: left;
    float: left;
    font-size: 105%;
    max-height: 38px;*/
}
/*#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0;
    max-height: 0;
    color: #eee;
    background: transparent;
    font-family: 'Droid Sans', Arial, sans-serif;
    font-size: 180%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000;
    letter-spacing: 0.9px;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0;
    max-height: 0;
    font-weight: bold;
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}*/

/* TOP MENU */
#top-bar {
    font-family: "Exo 2", Arial,sans-serif;
    font-size: 100%;
}

#top-bar a {
    color: #F2F2F2;
}

#top-bar ul li a {
    line-height: 0.2px;
    max-height: 1em;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #BCBCBC;
    color: #8E130C;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #AAA;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #EEE;
}

/* SIDE MENU */
#side-bar .side-block {
    border: none;
    border-radius: 5px;
    box-shadow: 0 1px 2px 1px rgba(0,0,0,.5); 
    background: #BCBCBC !important; /*override ugly inline bg colors from wikidot*/
    margin-bottom: 15px;
}

#side-bar .heading {
    color: #8E130C;
    border-bottom: 1px dashed #666;
    padding-left: 0.3em;
    font-family: 'Exo', Arial, sans-serif;
    font-size: 1.3em;
    font-weight: bold;
}

#side-bar div.menu-item {
    font-family: 'Droid Sans', Arial, sans-serif;
    color: #000;
}

#side-bar div.menu-item img {
    /*display: none;*/
}

div.social-media img {
    display: inline;
}

#side-bar div.menu-item a {
    font-family: "Droid Sans", Arial, sans-serif;
    color: #444;
}

/* 'USER RESOURCES' LINK-MENU IN SIDEBAR MENU */
#side-bar .collapsible-block-folded {
    /*background: none;*/
    background: url(http://scp-jp.wdfiles.com/local--files/deleted%3Acomponent%3Aresurrection-theme/expand.png) 0 2px no-repeat;
}

#side-bar .collapsible-block-link {
    margin-top: 3px;
    font-family: "Droid Sans, Arial, sans-serif";
    color: #444;
}	

/* When User Resources is clicked on, it reveals a link menu */
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #666;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-left: 0.3em;
    color: #8E130C;
    font-family: 'Exo', Arial, sans-serif;
    font-size: 1.3em;
    font-weight: bold;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #D11A0C;
}

/* CONTENT */
/*#main-content {
    margin: 0 4em 0 20em;
}

/* YUI-TABS */
.yui-navset .yui-content{
    background-color: #F2F2F2;
    border-radius: 5px;
    padding: 15px 35px;
    font-family: 'Droid Sans', Arial, sans-serif;
    font-size: 0.85rem;
    color: #666;
}

.yui-content a{
    color: #CC3300;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #F2F2F2 50%, #D8D8DA 50%) repeat scroll 0 0;
    color: #1A1A1A;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #FE9B26 50%, #FD6E20 50%) repeat scroll 0 0;
    color: #1A1A1A;
}

.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: rgba(0, 0, 0, 0) linear-gradient(to top, #F2F2F2 50%, #E3E3E4 50%) repeat scroll 0 0;
    color: #1A1A1A;
}

.yui-navset li {
    line-height: 1.3rem;
}

/* FOOTER */
/* PAGE OPTIONS - BOTTOM */
#page-info {
    color: #999;
}

a.action-area-close: hover{
    background-color: #FD6E20;
}

/* LICENSE AREA */
#license-area {
    color: #999;

}

/* EDIT LOCK */
#lock-info {
    background-color: #333;
    border: 1px solid #444;
    margin: 1em 0;
    padding: 0.5em 1em;
}

/* FILES OPTIONS */
table.page-files .highlight {
    background-color: #666;
}

div.buttons input, input.button, button, file, a.button {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #f2f2f2 50%, #d8d8da 50%) repeat scroll 0 0;
    color: #1a1a1a;
    border: medium none;
    margin: 0 .25em;
    padding: .25em;
    moz-border-radius: 1.5px;
    webkit-border-radius: 1.5px;
    border-radius: 1.5px;
    text-decoration: none;
}

#upload-file-list table .progress .progress-bar {
    background-color: #FD6E20;
    color: #000;
    height: 1.5em;
    line-height: 1.5em;
    width: 0;
    z-index: 1;
}

/* CONFIRMATION DIALOGUE BOX */
.owindow {
    background-color: #444;
    border: medium none;
    position: absolute;
    text-align: left;
    width: 50em;
}

.owindow .title {
    background-color: #1A1A1A;
    cursor: move;
    font-size: 120%;
    font-weight: bold;
    height: 1%;
    padding: 0.2em 1em;
}

.owindow .button-bar a {
    border: medium none;
    color: #333;
    margin: 0.4em;
    padding: 0.1em 1em;
    moz-border-radius: 1.5px;
    webkit-border-radius: 1.5px;
    border-radius: 1.5px;
    text-decoration: none;
}

/* SOME NICE BOXES */
div.sexy-box {
    border: none;
}

div.sexy-box div.image-container img {
    border: none;
}


/* CUSTOM PAGE CONTENT CLASSES */
/* Includes panels, etc */
.content-panel {
    border: none;
    border-radius: 5px;
    background-color: #F2F2F2;
    box-shadow: 0 1px 2px 1px rgba(0,0,0,.5); 
    /*box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.1);
    */
}

.content-panel a {
    color: #CC3300;
}

.content-panel.standalone {
    color: #666;
    background-color: #F2F2F2;
}

.content-panel h1, #page-title {
    color: #D11A0C;
    font-weight: normal;
    font-family: "Exo", Arial, sans-serif;
}

.content-panel h2, h3, h4, h5, h6{
    font-family: "Exo", Arial, sans-serif;
}

.content-panel h2{
    color: #1A1A1A;
}

.content-panel.standalone h3, .content-panel.standalone h4{
    color: #555;
}

.content-panel h4, h5, h6{
    font-weight: bold;
}

.content-panel.standalone h5, .content-panel.standalone h6{
    color: #333;
}

.content-panel.series {
    padding: 15px 35px;
    margin-bottom: 10px;
}

/* Page Rating Module Customizations */
.page-rate-widget-box {
    display: inline-block;
    margin-right: 2em;
}

div.page-rate-widget-box .rate-points {
    background-color: #D11A0C;
    border: solid 1px #D11A0C;
    border-radius: 3px 0 0 3px;
    moz-border-radius: 3px 0 0 3px;
    webkit-border-radius: 3px 0 0 3px;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    border-top: solid 1px #D11A0C;
    border-bottom: solid 1px #D11A0C;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    color: #D11A0C;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #D11A0C;
}

.page-rate-widget-box .cancel {
    background-color: #D11A0C;
    border: solid 1px #D11A0C;
    border-radius: 0 3px 3px 0;
    moz-border-radius: 0 3px 3px 0;
    webkit-border-radius: 0 3px 3px 0;
}

.page-rate-widget-box .cancel a:hover {
    background: #D11A0C;
}

/* unidentified
/* Heritage Collection Rating Module */

.heritage-rating-module .page-rate-widget-box {
    float: right;
}

.heritage-rating-module .heritage-emblem {
    float: right;
}
*/

/* webkit scrollbar */
::-webkit-scrollbar
{
    width: 9px;    /* for vertical scrollbars */
    height: 9px; /* for horizontal scrollbars */
    border: solid 1px rgba(0, 0, 0, 0.1);
    border-round: 0.5px;
}

::-webkit-scrollbar-track
{
    background: rgba(0, 0, 0, 0.1);
}

::-webkit-scrollbar-thumb
{
    background: rgba(50, 50, 50, 0.3);
}

.page-source {
    word-break: break-all;
    background-color: #f2f2f2;
}

/* Responsive Web Design */

@media (max-width: 767px) { 
#side-bar {
    background-color: rgba(209, 26, 13, .9);
}
}

/* Mobile Media Query */
@media (max-width: 479px) { 
#search-top-box {
    position: relative;
    left: 30%;
    top: 7em;
}

.page-options-bottom a {
    color: #DDD; 
}

.license-area {
    color: #333; 
}

#header {
    background-size: 90%;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) { 
#search-top-box {
    position: relative;
    left: 45%;
    top: 7em;
}

#header {
    background-size: 80%;
}
}

/* Mini Tablet Media Query */
@media (min-width: 581px) and (max-width: 767px) { 
#search-top-box {
    position: relative;
    left: 55%;
    top: 7em;
}

#header {
    background-size: 70%;
}
}

/* Tablet Media Query */
@media (min-width: 768px) and (max-width: 979px) {
#search-top-box {
    position: relative;
    left: 65%;
    top: 7em;
}

#header {
    background-size: 60%;
}
}

/* for the Interwiki */
#side-bar .scpnet-interwiki-wrapper iframe.scpnet-interwiki-frame {
    -webkit-filter: grayscale(100%) brightness(74%);
    filter: grayscale(100%) brightness(74%);
    border: none;
    border-radius: 5px;
    box-shadow: 0 1px 2px 1px rgba(0,0,0,.5); 
    margin-bottom: 15px;
}
[[/code]]
